// ì „ì‹œ ìˆ˜ì§‘ í•¨ìˆ˜
async function collectExhibitions() {
  log('ğŸ¨ Starting automated exhibition collection...');

  try {
    // 1. Tier 1 venues ì¡°íšŒ
    const { data: tier1Venues, error: venueError } = await supabase
      .from('venues')
      .select('name, tier')
      .eq('tier', '1')
      .eq('is_active', true);

    if (venueError) {
      log(`âŒ Error fetching venues: ${venueError.message}`);
      return;
    }

    log(`ğŸ“ Found ${tier1Venues.length} Tier 1 venues to process`);

    // 2. ê° venueë³„ ê²€ìƒ‰ ì‹¤í–‰
    const allExhibitions = [];

    for (const venue of tier1Venues) {
      log(`ğŸ” Searching exhibitions for: ${venue.name}`);

      const searchQuery = `${venue.name} ì „ì‹œ 2025`;
      const exhibitions = await searchNaverExhibitions(searchQuery);

      log(`ğŸ“Š Found ${exhibitions.length} exhibitions for ${venue.name}`);
      allExhibitions.push(...exhibitions);

      // API í˜¸ì¶œ ê°„ê²© (429 ì—ëŸ¬ ë°©ì§€)
      await new Promise(resolve => setTimeout(resolve, 500));
    }

    // 3. ì¤‘ë³µ ì œê±°
    const uniqueExhibitions = removeDuplicates(allExhibitions);
    log(`ğŸ”„ Removed duplicates: ${allExhibitions.length} -> ${uniqueExhibitions.length}`);

    // 4. Supabaseì— ì €ì¥
    const savedCount = await saveExhibitionsToSupabase(uniqueExhibitions);
    log(`âœ… Successfully saved ${savedCount} new exhibitions`);

    // 5. ì‘ì—… ê²°ê³¼ Railwayì— ë¡œê·¸
    await logCronJob('exhibition_collection', 'completed', {
      searched_venues: tier1Venues.length,
      found_exhibitions: uniqueExhibitions.length,
      saved_exhibitions: savedCount
    });

  } catch (error) {
    log(`âŒ Exhibition collection failed: ${error.message}`);

    // ì‹¤íŒ¨ ë¡œê·¸
    await logCronJob('exhibition_collection', 'failed', {
      error: error.message
    });
  }
}

// Naver API ê²€ìƒ‰ í•¨ìˆ˜
async function searchNaverExhibitions(query) {
  try {
    const [blogResponse, newsResponse] = await Promise.all([
      axios.get('https://openapi.naver.com/v1/search/blog.json', {
        params: { query, display: 50, sort: 'date' },
        headers: {
          'X-Naver-Client-Id': NAVER_CLIENT_ID,
          'X-Naver-Client-Secret': NAVER_CLIENT_SECRET
        }
      }),
      axios.get('https://openapi.naver.com/v1/search/news.json', {
        params: { query, display: 50, sort: 'date' },
        headers: {
          'X-Naver-Client-Id': NAVER_CLIENT_ID,
          'X-Naver-Client-Secret': NAVER_CLIENT_SECRET
        }
      })
    ]);

    const allItems = [
      ...blogResponse.data.items.map(item => ({ ...item, source: 'naver_blog' })),
      ...newsResponse.data.items.map(item => ({ ...item, source: 'naver_news' }))
    ];

    return parseExhibitionData(allItems);

  } catch (error) {
    log(`âŒ Naver API error: ${error.message}`);
    return [];
  }
}

// ì „ì‹œ ë°ì´í„° íŒŒì‹± í•¨ìˆ˜
function parseExhibitionData(items) {
  const exhibitions = [];

  const patterns = {
    titleBrackets: /\[(.*?)\]/g,
    titleQuotes: /[ã€Œ"'](.*?)[ã€"']/g,
    dateRange: /(\d{4})[ë…„.\s]*(\d{1,2})[ì›”.\s]*(\d{1,2})[ì¼]?\s*[-~]\s*(?:(\d{4})[ë…„.\s]*)?(\d{1,2})[ì›”.\s]*(\d{1,2})[ì¼]?/,
    venue: /(êµ­ë¦½í˜„ëŒ€ë¯¸ìˆ ê´€|ì„œìš¸ì‹œë¦½ë¯¸ìˆ ê´€|ë¦¬ì›€ë¯¸ìˆ ê´€|ì•„ëª¨ë ˆí¼ì‹œí”½ë¯¸ìˆ ê´€|ëŒ€ë¦¼ë¯¸ìˆ ê´€|ê°¤ëŸ¬ë¦¬í˜„ëŒ€|êµ­ì œê°¤ëŸ¬ë¦¬|PKMê°¤ëŸ¬ë¦¬|í˜ì´ìŠ¤ê°¤ëŸ¬ë¦¬|íƒ€ë°ìš°ìŠ¤ ë¡œíŒ|í˜ë¡œíƒ±|ì†¡ì€|ì•„íŠ¸ì„ ì¬ì„¼í„°|ë””ë®¤ì§€ì—„|ë°±ë‚¨ì¤€ì•„íŠ¸ì„¼í„°|ë¶€ì‚°ì‹œë¦½ë¯¸ìˆ ê´€|ë¶€ì‚°í˜„ëŒ€ë¯¸ìˆ ê´€)/
  };

  items.forEach(item => {
    const title = item.title.replace(/<[^>]*>/g, '').trim();
    const description = item.description.replace(/<[^>]*>/g, '').trim();
    const content = `${title} ${description}`;

    // ì œëª© ì¶”ì¶œ
    const titleMatches = [
      ...Array.from(content.matchAll(patterns.titleBrackets)),
      ...Array.from(content.matchAll(patterns.titleQuotes))
    ];

    // ë‚ ì§œ ì¶”ì¶œ
    const dateMatch = content.match(patterns.dateRange);
    let startDate = null, endDate = null;

    if (dateMatch) {
      const [, year1, month1, day1, year2, month2, day2] = dateMatch;
      startDate = `${year1}-${month1.padStart(2, '0')}-${day1.padStart(2, '0')}`;
      endDate = `${year2 || year1}-${month2.padStart(2, '0')}-${day2.padStart(2, '0')}`;
    }

    // ì¥ì†Œ ì¶”ì¶œ
    const venueMatch = content.match(patterns.venue);
    const venueName = venueMatch ? venueMatch[1] : null;

    // ìœ íš¨í•œ ì „ì‹œ ë°ì´í„°ê°€ ìˆëŠ” ê²½ìš°ë§Œ ì¶”ê°€
    if (titleMatches.length > 0 && venueName) {
      titleMatches.forEach(match => {
        const exhibitionTitle = match[1].trim();
        if (exhibitionTitle.length > 3) {
          exhibitions.push({
            title: exhibitionTitle,
            venueName,
            venueCity: 'ì„œìš¸',
            venueCountry: 'KR',
            startDate,
            endDate,
            description: description.substring(0, 500),
            source: item.source,
            sourceUrl: item.link
          });
        }
      });
    }
  });

  return exhibitions;
}

// ì¤‘ë³µ ì œê±° í•¨ìˆ˜
function removeDuplicates(exhibitions) {
  const seen = new Set();
  return exhibitions.filter(exhibition => {
    const key = `${exhibition.title}-${exhibition.venueName}-${exhibition.startDate}`;
    if (seen.has(key)) return false;
    seen.add(key);
    return true;
  });
}

// Supabase ì €ì¥ í•¨ìˆ˜
async function saveExhibitionsToSupabase(exhibitions) {
  let savedCount = 0;

  for (const exhibition of exhibitions) {
    try {
      // 1. Venue ID ì°¾ê¸°
      const { data: venue, error: venueError } = await supabase
        .from('venues')
        .select('id')
        .eq('name', exhibition.venueName)
        .single();

      if (venueError || !venue) continue;

      // 2. ì¤‘ë³µ ì²´í¬
      const { data: existing, error: checkError } = await supabase
        .from('exhibitions')
        .select('id')
        .eq('title', exhibition.title)
        .eq('venue_id', venue.id)
        .eq('start_date', exhibition.startDate)
        .maybeSingle();

      if (checkError || existing) continue;

      // 3. ì €ì¥
      const exhibitionRecord = {
        venue_id: venue.id,
        title: exhibition.title,
        venue_name: exhibition.venueName,
        venue_city: exhibition.venueCity,
        venue_country: exhibition.venueCountry,
        start_date: exhibition.startDate,
        end_date: exhibition.endDate,
        description: exhibition.description,
        source: exhibition.source,
        source_url: exhibition.sourceUrl,
        verification_status: 'verified',
        status: determineStatus(exhibition.startDate, exhibition.endDate)
      };

      const { error: saveError } = await supabase
        .from('exhibitions')
        .insert(exhibitionRecord);

      if (!saveError) {
        savedCount++;
      }

    } catch (error) {
      log(`âŒ Error saving exhibition: ${error.message}`);
    }
  }

  return savedCount;
}

// ì „ì‹œ ìƒíƒœ ê²°ì • í•¨ìˆ˜
function determineStatus(startDate, endDate) {
  if (!startDate || !endDate) return 'draft';

  const now = new Date();
  const start = new Date(startDate);
  const end = new Date(endDate);

  if (now < start) return 'upcoming';
  if (now > end) return 'ended';
  return 'ongoing';
}

// í¬ë¡  ì‘ì—… ë¡œê·¸ í•¨ìˆ˜
async function logCronJob(jobType, status, metadata = {}) {
  try {
    const client = await railwayPool.connect();

    await client.query(`
      INSERT INTO scraping_jobs (job_type, status, completed_at, results_count, metadata)
      VALUES ($1, $2, NOW(), $3, $4)
    `, [jobType, status, metadata.saved_exhibitions || 0, JSON.stringify(metadata)]);

    client.release();

  } catch (error) {
    log(`âŒ Error logging cron job: ${error.message}`);
  }
}

// ë©”ì¸ í¬ë¡  ì‘ì—… ìŠ¤ì¼€ì¤„ëŸ¬
function startCronJobs() {
  log('ğŸš€ Starting exhibition collection cron jobs...');

  // ë§¤ì¼ ì˜¤ì „ 9ì‹œì— Tier 1 venues ìˆ˜ì§‘
  cron.schedule('0 9 * * *', async () => {
    log('â° Daily Tier 1 collection started');
    await collectExhibitions();
  });

  // ë§¤ì£¼ ì›”ìš”ì¼ ì˜¤ì „ 10ì‹œì— Tier 2 venues ìˆ˜ì§‘
  cron.schedule('0 10 * * 1', async () => {
    log('â° Weekly Tier 2 collection started');
    // TODO: Tier 2 ìˆ˜ì§‘ ë¡œì§ êµ¬í˜„
  });

  // ë§¤ì›” 1ì¼ ì˜¤ì „ 11ì‹œì— Tier 3 venues ìˆ˜ì§‘
  cron.schedule('0 11 1 * *', async () => {
    log('â° Monthly Tier 3 collection started');
    // TODO: Tier 3 ìˆ˜ì§‘ ë¡œì§ êµ¬í˜„
  });

  log('âœ… All cron jobs scheduled successfully');
}

// í”„ë¡œê·¸ë¨ ì‹œì‘
if (require.main === module) {
  // ì¦‰ì‹œ ì‹¤í–‰ ëª¨ë“œ (í…ŒìŠ¤íŠ¸ìš©)
  if (process.argv.includes('--run-now')) {
    log('ğŸ§ª Running exhibition collection immediately...');
    collectExhibitions();
  } else {
    // í¬ë¡  ëª¨ë“œ
    startCronJobs();
  }
}

module.exports = {
  collectExhibitions,
  startCronJobs
};

const cron = require('node-cron');
const exhibitionCollectorService = require('../services/exhibitionCollectorService');
const Venue = require('../models/venue');
const { Op } = require('sequelize');

class ExhibitionCollectorCron {
  constructor() {
    this.dailyJobs = [];
    this.weeklyJobs = [];
  }

  start() {
    console.log('Starting exhibition collector cron jobs...');

    // Daily collection for Tier 1 venues (3 AM KST)
    this.dailyJobs.push(
      cron.schedule('0 3 * * *', async () => {
        console.log('Running daily exhibition collection...');
        await this.collectDailyExhibitions();
      }, {
        timezone: 'Asia/Seoul'
      })
    );

    // Twice weekly collection for Tier 2 venues (Tuesday & Friday, 3:30 AM KST)
    this.dailyJobs.push(
      cron.schedule('30 3 * * 2,5', async () => {
        console.log('Running twice-weekly exhibition collection...');
        await this.collectTwiceWeeklyExhibitions();
      }, {
        timezone: 'Asia/Seoul'
      })
    );

    // Weekly collection for Tier 3 venues (Sunday, 4 AM KST)
    this.weeklyJobs.push(
      cron.schedule('0 4 * * 0', async () => {
        console.log('Running weekly exhibition collection...');
        await this.collectWeeklyExhibitions();
      }, {
        timezone: 'Asia/Seoul'
      })
    );

    // Clean up old exhibitions (Daily at 2 AM KST)
    this.dailyJobs.push(
      cron.schedule('0 2 * * *', async () => {
        console.log('Cleaning up old exhibitions...');
        await this.cleanupOldExhibitions();
      }, {
        timezone: 'Asia/Seoul'
      })
    );

    console.log('Exhibition collector cron jobs started!');
  }

  async collectDailyExhibitions() {
    try {
      const venues = await Venue.findAll({
        where: {
          isActive: true,
          tier: '1',
          crawlFrequency: 'daily'
        }
      });

      console.log(`Collecting exhibitions for ${venues.length} Tier 1 venues...`);

      for (const venue of venues) {
        try {
          const results = await exhibitionCollectorService.searchNaverForVenue(venue);
          console.log(`Collected ${results.length} exhibitions for ${venue.name}`);

          // Update last crawled timestamp
          venue.lastCrawledAt = new Date();
          await venue.save();

          // Rate limiting
          await this.delay(2000);
        } catch (error) {
          console.error(`Failed to collect for ${venue.name}:`, error);
        }
      }

      // Collect international exhibitions
      if (process.env.ENABLE_INTERNATIONAL_COLLECTION === 'true') {
        await this.collectInternationalExhibitions();
      }

    } catch (error) {
      console.error('Daily collection error:', error);
    }
  }

  async collectTwiceWeeklyExhibitions() {
    try {
      const venues = await Venue.findAll({
        where: {
          isActive: true,
          tier: '2',
          crawlFrequency: 'twice_weekly'
        }
      });

      console.log(`Collecting exhibitions for ${venues.length} Tier 2 venues...`);

      for (const venue of venues) {
        try {
          const results = await exhibitionCollectorService.searchNaverForVenue(venue);
          console.log(`Collected ${results.length} exhibitions for ${venue.name}`);

          venue.lastCrawledAt = new Date();
          await venue.save();

          await this.delay(2000);
        } catch (error) {
          console.error(`Failed to collect for ${venue.name}:`, error);
        }
      }
    } catch (error) {
      console.error('Twice-weekly collection error:', error);
    }
  }

  async collectWeeklyExhibitions() {
    try {
      const venues = await Venue.findAll({
        where: {
          isActive: true,
          tier: '3',
          crawlFrequency: 'weekly'
        }
      });

      console.log(`Collecting exhibitions for ${venues.length} Tier 3 venues...`);

      for (const venue of venues) {
        try {
          const results = await exhibitionCollectorService.searchNaverForVenue(venue);
          console.log(`Collected ${results.length} exhibitions for ${venue.name}`);

          venue.lastCrawledAt = new Date();
          await venue.save();

          await this.delay(2000);
        } catch (error) {
          console.error(`Failed to collect for ${venue.name}:`, error);
        }
      }
    } catch (error) {
      console.error('Weekly collection error:', error);
    }
  }

  async collectInternationalExhibitions() {
    try {
      console.log('Collecting international exhibitions...');

      // Priority cities
      const cities = ['New York', 'London', 'Paris', 'Tokyo', 'Hong Kong'];

      for (const city of cities) {
        const venues = await Venue.findAll({
          where: {
            isActive: true,
            city,
            country: { [Op.ne]: 'KR' }
          }
        });

        for (const venue of venues) {
          // This would use web scraping or other APIs
          // For now, using placeholder
          console.log(`Would collect exhibitions for ${venue.name} in ${city}`);
          await this.delay(3000);
        }
      }
    } catch (error) {
      console.error('International collection error:', error);
    }
  }

  async cleanupOldExhibitions() {
    try {
      const Exhibition = require('../models/exhibition');

      // Update exhibition statuses
      const now = new Date();

      // Mark ended exhibitions
      await Exhibition.update(
        { status: 'ended' },
        {
          where: {
            endDate: { [Op.lt]: now },
            status: { [Op.ne]: 'ended' }
          }
        }
      );

      // Mark ongoing exhibitions
      await Exhibition.update(
        { status: 'ongoing' },
        {
          where: {
            startDate: { [Op.lte]: now },
            endDate: { [Op.gte]: now },
            status: { [Op.ne]: 'ongoing' }
          }
        }
      );

      // Delete very old unverified exhibitions (older than 6 months)
      const sixMonthsAgo = new Date();
      sixMonthsAgo.setMonth(sixMonthsAgo.getMonth() - 6);

      const deleted = await Exhibition.destroy({
        where: {
          createdAt: { [Op.lt]: sixMonthsAgo },
          verificationStatus: { [Op.ne]: 'verified' }
        }
      });

      console.log(`Cleaned up ${deleted} old unverified exhibitions`);
    } catch (error) {
      console.error('Cleanup error:', error);
    }
  }

  delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  stop() {
    console.log('Stopping exhibition collector cron jobs...');

    this.dailyJobs.forEach(job => job.stop());
    this.weeklyJobs.forEach(job => job.stop());

    this.dailyJobs = [];
    this.weeklyJobs = [];
  }
}

module.exports = new ExhibitionCollectorCron();

