class CollectionReportGenerator {
  constructor() {
    this.targetArtists = [
      // ìš°ì„ ìˆœìœ„ ì•„í‹°ìŠ¤íŠ¸ (20ëª…)
      'Leonardo da Vinci', 'Pablo Picasso', 'Vincent van Gogh', 'Claude Monet', 'Frida Kahlo',
