      const existing = await client.query(
        'SELECT id FROM exhibitions WHERE title_en = $1 AND venue_name = $2 AND start_date = $3',
        [exhibition.title_en, exhibition.venue_name, exhibition.start_date]
      );

      if (existing.rows.length > 0) {
        this.stats.skipped++;
