    await this.respectRateLimit();

    try {
      console.log(`ğŸ”„ Fetching: ${url}`);
      const response = await axios.get(url, {
        headers: {
          'User-Agent': this.userAgent,
          'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
          'Accept-Language': 'en-US,en;q=0.9'
        },
        timeout: 20000
      });
      return response.data;
    } catch (error) {
      console.error(`âŒ Error fetching ${url}:`, error.message);
      return null;
    }
  }

  async crawlMuseum(museum, city) {
    console.log(`\nğŸ›ï¸  Crawling ${museum.name}...`);

    const html = await this.fetchPage(museum.url);
    if (!html) return [];

    const $ = cheerio.load(html);
    const exhibitions = [];

    // ë‹¤ì–‘í•œ ì„ íƒìë¡œ ì „ì‹œ ì°¾ê¸°
    const exhibitionSelectors = museum.selectors.exhibitions.split(', ');

    for (const selector of exhibitionSelectors) {
      console.log(`   Trying selector: ${selector}`);
      const found = $(selector);
      console.log(`   Found: ${found.length} elements`);

      if (found.length > 0) {
        found.each((i, element) => {
          if (exhibitions.length >= 10) return false; // ìµœëŒ€ 10ê°œ

          const $el = $(element);

          // ì œëª© ì¶”ì¶œ
          const title = this.extractText($el, museum.selectors.title);

          // ë‚ ì§œ ì¶”ì¶œ
          const dates = this.extractText($el, museum.selectors.dates);

          // ì„¤ëª… ì¶”ì¶œ
          const description = this.extractText($el, museum.selectors.description);

          // ë§í¬ ì¶”ì¶œ
          const link = $el.find('a').first().attr('href');
          let fullUrl = '';
          if (link) {
            try {
              fullUrl = link.startsWith('http') ? link : new URL(link, museum.url).href;
            } catch (e) {
              fullUrl = museum.url + link;
            }
          }

          if (title && title.length > 3) {
            exhibitions.push({
              title: this.cleanText(title),
              venue: {
                name: museum.name,
                city,
                url: museum.url
              },
              dates: {
                original: this.cleanText(dates)
              },
              description: this.cleanText(description).substring(0, 500),
              url: fullUrl,
              city,
              source: 'major_museum_official',
              quality: 'world_class', // ì£¼ìš” ë¯¸ìˆ ê´€ì´ë¯€ë¡œ ìµœê³  í’ˆì§ˆ
              crawledAt: new Date().toISOString()
            });
          }
        });

        if (exhibitions.length > 0) {
          console.log(`   âœ… Success with selector "${selector}"`);
          break;
        }
      }
    }

    console.log(`   ğŸ“Š Found ${exhibitions.length} exhibitions`);
    return exhibitions;
  }

  extractText($el, selectors) {
    const selectorList = selectors.split(', ');
    for (const sel of selectorList) {
      const text = $el.find(sel).first().text().trim();
      if (text) return text;
    }
    return $el.text().trim();
  }

  cleanText(text) {
    if (!text) return '';
    return text
      .replace(/\s+/g, ' ')
      .replace(/\n/g, ' ')
      .trim()
      .substring(0, 300);
  }

  async crawlAllMajorMuseums() {
    console.log('ğŸŒŸ MAJOR MUSEUMS OFFICIAL SITES CRAWLER');
    console.log('======================================\n');
    console.log('ëª©í‘œ: ì„¸ê³„ ì£¼ìš” ë¯¸ìˆ ê´€ ê³µì‹ ì‚¬ì´íŠ¸ì—ì„œ í˜„ì¬ ì „ì‹œ ìˆ˜ì§‘');
    console.log('í’ˆì§ˆ: WORLD-CLASS - ì§ì ‘ ê³µì‹ ì†ŒìŠ¤ì—ì„œ ìˆ˜ì§‘\n');

    const startTime = Date.now();
    const allExhibitions = [];
    const results = {};

    for (const [city, museums] of Object.entries(this.majorMuseums)) {
      console.log(`\nğŸ“ ${city.toUpperCase()} MAJOR MUSEUMS`);
      console.log('='.repeat(40));

      results[city] = [];

      for (const museum of museums) {
        try {
          const exhibitions = await this.crawlMuseum(museum, city);
          results[city].push(...exhibitions);
          allExhibitions.push(...exhibitions);
        } catch (error) {
          console.error(`âŒ Error crawling ${museum.name}:`, error.message);
        }
      }

      console.log(`\nğŸ¯ ${city} ì´ ìˆ˜ì§‘: ${results[city].length}ê°œ ì „ì‹œ`);
    }

    const endTime = Date.now();
    const duration = Math.round((endTime - startTime) / 1000);

    // ê²°ê³¼ ì €ì¥
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const filename = `major-museums-official-${timestamp}.json`;

    const finalResult = {
      metadata: {
        collectionDate: new Date().toISOString(),
        totalExhibitions: allExhibitions.length,
        citiesCovered: Object.keys(this.majorMuseums).length,
        museumsCovered: Object.values(this.majorMuseums).flat().length,
        durationSeconds: duration,
        quality: 'WORLD-CLASS - Official museum sources'
      },
      cityResults: results,
      allExhibitions
    };

    fs.writeFileSync(filename, JSON.stringify(finalResult, null, 2));

    // ìµœì¢… ë³´ê³ ì„œ
    console.log(`\nğŸ‰ MAJOR MUSEUMS OFFICIAL COLLECTION COMPLETED!`);
    console.log('===============================================');
    console.log(`â° Duration: ${Math.floor(duration / 60)}m ${duration % 60}s`);
    console.log(`ğŸ›ï¸  Museums crawled: ${Object.values(this.majorMuseums).flat().length}`);
    console.log(`ğŸ“Š Total exhibitions: ${allExhibitions.length}`);
    console.log(`ğŸ’ Quality: WORLD-CLASS (official sources)`);
    console.log(`ğŸ’¾ Saved to: ${filename}`);

    // ë„ì‹œë³„ ê²°ê³¼
    console.log(`\nğŸ† Results by City:`);
    Object.entries(results).forEach(([city, exhibitions]) => {
      console.log(`   ${city}: ${exhibitions.length} exhibitions`);
    });

    // ìƒ˜í”Œ ì „ì‹œ ì¶œë ¥
    if (allExhibitions.length > 0) {
      console.log(`\nâœ¨ Sample World-Class Exhibitions:`);
      allExhibitions.slice(0, 8).forEach((ex, i) => {
        console.log(`   ${i + 1}. "${ex.title}" - ${ex.venue.name} (${ex.city})`);
      });
    }

    return finalResult;
  }
}

