        exhibition.exhibition_type,
        exhibition.official_url,
        exhibition.source
      ]);

      console.log(`   âœ… "${exhibition.title_local}" - ${exhibition.venue_name}`);
      this.stats.inserted++;

    } catch (error) {
      console.error(`   âŒ "${exhibition.title_local}" ì…ë ¥ ì‹¤íŒ¨:`, error.message);
      this.stats.errors++;
    }
