      unmatched: 0
    };

    for (const row of artveeArtists.rows) {
      const artveeArtist = row.artist;
      let matched = false;

      // 1. ìˆ˜ë™ ë§¤í•‘ í™•ì¸
      const manualMatch = await matchManual(artveeArtist);
      if (manualMatch) {
        // ìˆ˜ë™ ë§¤í•‘ëœ ì´ë¦„ìœ¼ë¡œ ë‹¤ì‹œ ê²€ìƒ‰
        const artistResult = await client.query(`
