class PriorityVenuesCollector {
  constructor() {
    this.apiKey = process.env.GOOGLE_PLACES_API_KEY;
    this.baseUrl = 'https://maps.googleapis.com/maps/api/place';
    this.stats = {
      processed: 0,
      updated: 0,
      notFound: 0,
      errors: 0
