  }

  async collectPriorityVenues() {
    console.log('ğŸ¯ ì£¼ìš” ë¯¸ìˆ ê´€ ìš°ì„  ìˆ˜ì§‘ (Google Places API)');
    console.log(`ğŸ”‘ API í‚¤: ${this.apiKey ? 'ì„¤ì •ë¨' : 'ì—†ìŒ'}`);

    if (!this.apiKey) {
      console.log('âŒ Google Places API í‚¤ê°€ í•„ìš”í•©ë‹ˆë‹¤.');
      return;
    }

    const client = await pool.connect();

    try {
      // Tier 1 ë¯¸ìˆ ê´€ë“¤ë§Œ ìš°ì„  ì²˜ë¦¬
      const priorityVenues = await client.query(`
        SELECT id, name, city, country, website
        FROM venues 
        WHERE tier = 1 AND is_active = true
        ORDER BY CASE 
          WHEN country = 'KR' THEN 1
          WHEN country = 'US' THEN 2
          WHEN country = 'GB' THEN 3
          ELSE 4
        END, name
        LIMIT 50
      `);

      console.log(`\nğŸ›ï¸ ${priorityVenues.rows.length}ê°œ ì£¼ìš” ë¯¸ìˆ ê´€ ì²˜ë¦¬ ì‹œì‘\n`);

      for (const venue of priorityVenues.rows) {
        await this.processVenue(venue, client);

        // API í˜¸ì¶œ ì œí•œ (1ì´ˆ ë”œë ˆì´)
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      await this.showResults(client);

    } catch (error) {
      console.error('âŒ ìˆ˜ì§‘ ì¤‘ ì˜¤ë¥˜:', error);
    } finally {
      client.release();
