      }

      // ìƒì„¸ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
      const details = await this.getPlaceDetails(placeData.place_id);

      if (!details) {
        console.log(`   âŒ ìƒì„¸ ì •ë³´ ê°€ì ¸ì˜¤ê¸° ì‹¤íŒ¨`);
        this.stats.errors++;
