      }

      // ë°ì´í„°ë² ì´ìŠ¤ ì—…ë°ì´íŠ¸
      await this.updateVenueInDatabase(venue.id, details, client);

      const info = [
        details.rating ? `í‰ì : ${details.rating}` : null,
        details.user_ratings_total ? `ë¦¬ë·°: ${details.user_ratings_total.toLocaleString()}ê°œ` : null,
        details.formatted_phone_number ? 'ì „í™”ë²ˆí˜¸ âœ“' : null,
        details.website ? 'ì›¹ì‚¬ì´íŠ¸ âœ“' : null
