    console.log(`\nğŸ“ˆ Tier 1 ë¯¸ìˆ ê´€ ë°ì´í„° í’ˆì§ˆ:`);
    console.log(`   ì¢Œí‘œ ì •ë³´: ${stats.rows[0].with_coordinates}/${stats.rows[0].total}ê°œ`);
    console.log(`   í‰ì  ì •ë³´: ${stats.rows[0].with_rating}/${stats.rows[0].total}ê°œ`);
