  }

  /**
   * í”„ë¡œí•„ ì´ë¯¸ì§€ë¡œ ì„¤ì •
   */
  async setAsProfileImage(req, res) {
    try {
      const { profileId } = req.params;
      const { userId } = req;

      // ì†Œìœ ê¶Œ í™•ì¸
      const checkQuery = `
        SELECT transformed_image 
        FROM art_profiles 
