// ë©”ëª¨ë¦¬ ìºì‹œ ì„¤ì •
const cache = new Map();
const CACHE_DURATION = 5 * 60 * 1000; // 5ë¶„

function getCacheKey(req) {
  const params = new URLSearchParams(req.query);
  return `exhibitions:${params.toString()}`;
}

function getFromCache(key) {
  const cached = cache.get(key);
  if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
    return cached.data;
  }
  return null;
}

function setCache(key, data) {
  cache.set(key, {
    data,
    timestamp: Date.now()
  });

  // ìºì‹œ ì •ë¦¬ (10ë¶„ë§ˆë‹¤)
  if (cache.size > 100) {
    const now = Date.now();
    for (const [key, value] of cache.entries()) {
      if (now - value.timestamp > CACHE_DURATION * 2) {
        cache.delete(key);
      }
    }
  }
}

const exhibitionController = {
  // Get exhibitions with filters
  async getExhibitions(req, res) {
    try {
      // ìºì‹œ í™•ì¸
      const cacheKey = getCacheKey(req);
      const cachedData = getFromCache(cacheKey);
      if (cachedData) {
        return res.json(cachedData);
      }

      const {
        page = 1,
        limit = 20,
        status,
        city,
        venue_id,
        search,
        sort = 'created_at',
        order = 'desc'
      } = req.query;

      // í˜ì´ì§€ë„¤ì´ì…˜ ì„¤ì •
      const offset = (page - 1) * limit;

      // Build base query
      let query = `
        SELECT e.*, v.id as venue_id, v.name as venue_name, v.name_en as venue_name_en,
               v.city as venue_city, v.country as venue_country, v.venue_type as venue_type,
               v.website as venue_website, v.social_media->>'instagram' as venue_instagram
        FROM exhibitions e
        INNER JOIN global_venues v ON e.venue_id = v.id
        WHERE 1=1
      `;
      const queryParams = [];
      let paramIndex = 1;

      // Apply filters
      if (status) {
        query += ` AND e.status = $${paramIndex}`;
        queryParams.push(status);
        paramIndex++;
      }

      if (city) {
        query += ` AND e.venue_city = $${paramIndex}`;
        queryParams.push(city);
        paramIndex++;
      }

      if (venue_id) {
        query += ` AND e.venue_id = $${paramIndex}`;
        queryParams.push(venue_id);
        paramIndex++;
      }

      if (search) {
        query += ` AND (e.title ILIKE $${paramIndex} OR e.description ILIKE $${paramIndex} OR v.name ILIKE $${paramIndex})`;
        queryParams.push(`%${search}%`);
        paramIndex++;
      }

      // Add sorting
      const validSorts = ['created_at', 'start_date', 'end_date', 'title', 'views'];
      const sortColumn = validSorts.includes(sort) ? sort : 'created_at';
      const sortOrder = order === 'asc' ? 'ASC' : 'DESC';
      query += ` ORDER BY e.${sortColumn} ${sortOrder}`;

      // Add pagination
      query += ` LIMIT $${paramIndex} OFFSET $${paramIndex + 1}`;
      queryParams.push(parseInt(limit), offset);

      const result = await pool.query(query, queryParams);
      const exhibitions = result.rows;

      // Get total count
      let countQuery = `
        SELECT COUNT(*) as total
        FROM exhibitions e
        INNER JOIN venues v ON e.venue_id = v.id
        WHERE 1=1
      `;
      const countParams = [];
      let countParamIndex = 1;

      if (status) {
        countQuery += ` AND e.status = $${countParamIndex}`;
        countParams.push(status);
        countParamIndex++;
      }
      if (city) {
        countQuery += ` AND e.venue_city = $${countParamIndex}`;
        countParams.push(city);
        countParamIndex++;
      }
      if (venue_id) {
        countQuery += ` AND e.venue_id = $${countParamIndex}`;
        countParams.push(venue_id);
        countParamIndex++;
      }
      if (search) {
        countQuery += ` AND (e.title ILIKE $${countParamIndex} OR e.description ILIKE $${countParamIndex} OR v.name ILIKE $${countParamIndex})`;
        countParams.push(`%${search}%`);
        countParamIndex++;
      }

      const countResult = await pool.query(countQuery, countParams);
      const count = parseInt(countResult.rows[0].total);

      // Get status statistics
      const statsResult = await pool.query('SELECT status, COUNT(*) as count FROM exhibitions GROUP BY status');
      const stats = statsResult.rows.reduce((acc, row) => {
        acc[row.status] = parseInt(row.count);
        return acc;
      }, {});

      const response = {
        success: true,
        data: exhibitions,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total: count,
          pages: Math.ceil(count / limit)
        },
        stats
      };

      // ìºì‹œì— ì €ì¥
      setCache(cacheKey, response);

      res.json(response);

    } catch (error) {
      console.error('Error in getExhibitions:', error);
      res.status(500).json({
        success: false,
        message: 'Internal server error'
      });
    }
  },

