  });

  // ìŠ¤íŠ¸ë¦­ ë³´ìƒ í™•ì¸ (ë§¤ì¼ ë°¤ 11ì‹œ)
  cron.schedule('0 23 * * *', () => {
    checkStreakRewards();
  }, {
