  });

  // ì¼ì¼ ì¶”ì²œ ì‘í’ˆ í ìƒì„± (ë§¤ì¼ ìƒˆë²½ 2ì‹œ)
  cron.schedule('0 2 * * *', () => {
    generateDailyArtworkQueue();
