  });

  // ì£¼ê°„ ë¦¬í¬íŠ¸ ìƒì„± (ì¼ìš”ì¼ ë°¤ 11ì‹œ)
  cron.schedule('0 23 * * 0', () => {
    generateWeeklyReports();
  }, {
