  if (!validPurposes.includes(purpose)) {
    return res.status(400).json({ error: 'Invalid purpose' });
  }

  const filteredUsers = demoUsers.filter(user =>
    user.id !== req.user.userId && user.user_purpose === purpose
  );

  res.json({
    purpose,
    users: filteredUsers,
    total: filteredUsers.length
  });
});

