/**
 * APT í”„ë¡œí•„ êµ¬ì¡° ê²€ì¦
 */
function validateAPTProfile(aptProfile) {
  if (!aptProfile) {
    throw new APTValidationError('APT í”„ë¡œí•„ì´ nullì…ë‹ˆë‹¤');
  }

  // primary_types ê²€ì¦
  if (!aptProfile.primary_types || !Array.isArray(aptProfile.primary_types)) {
    throw new APTValidationError('primary_types ë°°ì—´ì´ í•„ìš”í•©ë‹ˆë‹¤');
  }

  if (aptProfile.primary_types.length === 0) {
    throw new APTValidationError('ìµœì†Œ 1ê°œì˜ primary_typeì´ í•„ìš”í•©ë‹ˆë‹¤');
  }

  // ê° íƒ€ì… ê²€ì¦
  aptProfile.primary_types.forEach((typeObj, index) => {
    if (!typeObj.type) {
      throw new APTValidationError(`primary_types[${index}]ì— typeì´ ì—†ìŠµë‹ˆë‹¤`);
    }

    // ìœ íš¨í•œ SAYU íƒ€ì…ì¸ì§€ í™•ì¸
    if (!VALID_TYPE_CODES.includes(typeObj.type)) {
      throw new APTValidationError(
        `ìœ íš¨í•˜ì§€ ì•Šì€ APT íƒ€ì…: ${typeObj.type}`,
        {
          invalidType: typeObj.type,
          validTypes: VALID_TYPE_CODES
        }
      );
    }

    // íƒ€ì… ì •ë³´ ê°€ì ¸ì˜¤ê¸°
    const sayuType = getSAYUType(typeObj.type);

    // í•„ìˆ˜ í•„ë“œ ê²€ì¦
    const requiredFields = ['title', 'animal', 'confidence'];
    requiredFields.forEach(field => {
      if (!typeObj[field] && typeObj[field] !== 0) {
        throw new APTValidationError(
          `primary_types[${index}]ì— ${field}ê°€ ì—†ìŠµë‹ˆë‹¤`
        );
      }
    });

    // confidence ë²”ìœ„ ê²€ì¦
    if (typeObj.confidence < 0 || typeObj.confidence > 100) {
      throw new APTValidationError(
        `confidenceëŠ” 0-100 ì‚¬ì´ì—¬ì•¼ í•©ë‹ˆë‹¤: ${typeObj.confidence}`
      );
    }
  });

  // dimensions ê²€ì¦
  if (!aptProfile.dimensions) {
    throw new APTValidationError('dimensionsê°€ í•„ìš”í•©ë‹ˆë‹¤');
  }

  const requiredDimensions = ['L', 'S', 'A', 'R', 'E', 'M', 'F', 'C'];
  requiredDimensions.forEach(dim => {
    if (typeof aptProfile.dimensions[dim] !== 'number') {
      throw new APTValidationError(`dimension ${dim}ì´ ì—†ê±°ë‚˜ ìˆ«ìê°€ ì•„ë‹™ë‹ˆë‹¤`);
    }
    if (aptProfile.dimensions[dim] < 0 || aptProfile.dimensions[dim] > 100) {
      throw new APTValidationError(
        `dimension ${dim}ì€ 0-100 ì‚¬ì´ì—¬ì•¼ í•©ë‹ˆë‹¤: ${aptProfile.dimensions[dim]}`
      );
    }
  });

  // meta ê²€ì¦
  if (!aptProfile.meta) {
    throw new APTValidationError('meta ì •ë³´ê°€ í•„ìš”í•©ë‹ˆë‹¤');
  }

  if (!aptProfile.meta.analysis_method) {
    throw new APTValidationError('analysis_methodê°€ í•„ìš”í•©ë‹ˆë‹¤');
  }

  if (!aptProfile.meta.analysis_date) {
    throw new APTValidationError('analysis_dateê°€ í•„ìš”í•©ë‹ˆë‹¤');
  }

  return true;
}

/**
 * ì¤‘ìš”ë„ê°€ ë†’ì€ ì•„í‹°ìŠ¤íŠ¸ì˜ APT ì œê±° ë°©ì§€
 */
function preventImportantArtistAPTDeletion(artist, newAPTProfile) {
  // ì¤‘ìš”ë„ 90 ì´ìƒì¸ ì•„í‹°ìŠ¤íŠ¸ì˜ APTë¥¼ nullë¡œ ì„¤ì •í•˜ë ¤ëŠ” ê²½ìš°
  if (artist.importance_score >= 90 && artist.apt_profile && !newAPTProfile) {
    throw new APTValidationError(
