/**
 * Enhanced validation middleware for exhibition endpoints
 * Provides comprehensive input validation and sanitization for exhibition data
 */

// Exhibition creation/update validation
const exhibitionValidation = [
  body('title')
    .isLength({ min: 2, max: 200 })
    .withMessage('Title must be between 2 and 200 characters')
    .trim()
    .escape()
    .custom((value) => {
      // Check for suspicious patterns
      if (/<script|javascript:|data:/gi.test(value)) {
        throw new Error('Title contains invalid content');
      }
      return true;
    }),

  body('description')
    .optional()
    .isLength({ max: 2000 })
    .withMessage('Description must be less than 2000 characters')
    .trim()
    .escape(),

  body('venue_name')
    .isLength({ min: 1, max: 100 })
    .withMessage('Venue name is required and must be less than 100 characters')
    .trim()
    .escape(),

  body('venue_address')
    .optional()
    .isLength({ max: 300 })
    .withMessage('Venue address must be less than 300 characters')
    .trim(),

  body('venue_city')
    .isLength({ min: 1, max: 50 })
    .withMessage('City is required and must be less than 50 characters')
    .trim()
    .escape(),

  body('venue_phone')
    .optional()
    .isMobilePhone('any')
    .withMessage('Invalid phone number format'),

  body('start_date')
    .isISO8601()
    .withMessage('Start date must be a valid ISO 8601 date')
    .custom((value) => {
      const date = new Date(value);
      const now = new Date();
      if (date < now.setHours(0, 0, 0, 0)) {
        throw new Error('Start date cannot be in the past');
      }
      return true;
    }),

  body('end_date')
    .isISO8601()
    .withMessage('End date must be a valid ISO 8601 date')
    .custom((value, { req }) => {
      const startDate = new Date(req.body.start_date);
      const endDate = new Date(value);
      if (endDate <= startDate) {
        throw new Error('End date must be after start date');
      }
      return true;
    }),

  body('admission_fee')
    .optional()
    .isFloat({ min: 0, max: 1000000 })
    .withMessage('Admission fee must be a positive number')
    .toFloat(),

  body('website_url')
    .optional()
    .isURL({ protocols: ['http', 'https'] })
    .withMessage('Website URL must be valid')
    .custom((value) => {
      // Additional URL validation
      try {
        const url = new URL(value);
        if (!['http:', 'https:'].includes(url.protocol)) {
          throw new Error('Invalid protocol');
        }
        return true;
      } catch (error) {
        throw new Error('Invalid URL format');
      }
    }),

  body('image_url')
    .optional()
    .isURL({ protocols: ['http', 'https'] })
    .withMessage('Image URL must be valid')
    .custom((value) => {
      // Validate image URL format
      const imageExtensions = ['.jpg', '.jpeg', '.png', '.webp', '.gif'];
      const lowercaseUrl = value.toLowerCase();
      const hasValidExtension = imageExtensions.some(ext =>
        lowercaseUrl.includes(ext) || lowercaseUrl.includes('cloudinary.com')
      );

      if (!hasValidExtension) {
        throw new Error('Image URL must point to a valid image file');
      }
      return true;
    }),

  body('tags')
    .optional()
    .isArray({ max: 20 })
    .withMessage('Tags must be an array with maximum 20 items')
    .custom((tags) => {
      if (tags.some(tag =>
        typeof tag !== 'string' ||
        tag.length < 1 ||
        tag.length > 50 ||
        /<script|javascript:/gi.test(tag)
      )) {
        throw new Error('Invalid tag format');
      }
      return true;
    }),

  body('status')
    .optional()
    .isIn(['upcoming', 'ongoing', 'ended'])
    .withMessage('Status must be one of: upcoming, ongoing, ended'),

  body('latitude')
    .optional()
    .isFloat({ min: -90, max: 90 })
    .withMessage('Latitude must be between -90 and 90'),

  body('longitude')
    .optional()
    .isFloat({ min: -180, max: 180 })
    .withMessage('Longitude must be between -180 and 180')
];

// Exhibition submission validation
const exhibitionSubmissionValidation = [
  param('id')
    .isUUID()
    .withMessage('Exhibition ID must be a valid UUID'),

  body('submitter_name')
    .isLength({ min: 2, max: 100 })
    .withMessage('Submitter name must be between 2 and 100 characters')
    .trim()
    .escape(),

  body('submitter_email')
    .isEmail()
    .normalizeEmail()
    .withMessage('Valid email is required'),

  body('submitter_phone')
    .optional()
    .isMobilePhone('any')
    .withMessage('Invalid phone number format'),

  body('organization')
    .optional()
    .isLength({ max: 200 })
    .withMessage('Organization name must be less than 200 characters')
    .trim()
    .escape(),

  body('description')
    .optional()
    .isLength({ max: 1000 })
    .withMessage('Description must be less than 1000 characters')
    .trim()
    .escape(),

  body('additional_info')
    .optional()
    .isLength({ max: 2000 })
    .withMessage('Additional info must be less than 2000 characters')
    .trim()
    .escape()
];

// Exhibition query validation
const exhibitionQueryValidation = [
  query('page')
    .optional()
    .isInt({ min: 1, max: 1000 })
    .withMessage('Page must be between 1 and 1000')
    .toInt(),

  query('limit')
    .optional()
    .isInt({ min: 1, max: 100 })
    .withMessage('Limit must be between 1 and 100')
    .toInt(),

  query('city')
    .optional()
    .isLength({ min: 1, max: 50 })
    .withMessage('City filter must be between 1 and 50 characters')
    .trim()
    .escape(),

  query('status')
    .optional()
    .isIn(['upcoming', 'ongoing', 'ended', 'all'])
    .withMessage('Status must be one of: upcoming, ongoing, ended, all'),

  query('search')
    .optional()
    .isLength({ min: 1, max: 100 })
    .withMessage('Search term must be between 1 and 100 characters')
    .trim()
    .escape()
    .custom((value) => {
      // Prevent SQL injection patterns in search
      const dangerousPatterns = [
        /(\bUNION\b|\bSELECT\b|\bINSERT\b|\bUPDATE\b|\bDELETE\b|\bDROP\b)/gi,
        /(\-\-|\#|\/\*|\*\/)/gi,
        /(\bOR\b|\bAND\b)\s+\d+\s*=\s*\d+/gi
      ];

      if (dangerousPatterns.some(pattern => pattern.test(value))) {
        throw new Error('Search term contains invalid characters');
      }
      return true;
    }),

  query('sort')
    .optional()
    .isIn(['date', 'popularity', 'alphabetical', 'relevance'])
    .withMessage('Sort must be one of: date, popularity, alphabetical, relevance'),

  query('order')
    .optional()
    .isIn(['asc', 'desc'])
    .withMessage('Order must be asc or desc'),

  query('date_from')
    .optional()
    .isISO8601()
    .withMessage('Date from must be a valid ISO 8601 date'),

  query('date_to')
    .optional()
    .isISO8601()
    .withMessage('Date to must be a valid ISO 8601 date')
    .custom((value, { req }) => {
      if (req.query.date_from) {
        const dateFrom = new Date(req.query.date_from);
        const dateTo = new Date(value);
        if (dateTo <= dateFrom) {
          throw new Error('Date to must be after date from');
        }
      }
      return true;
    }),

  query('price_max')
    .optional()
    .isFloat({ min: 0, max: 1000000 })
    .withMessage('Maximum price must be a positive number')
    .toFloat(),

  query('has_image')
    .optional()
    .isBoolean()
    .withMessage('Has image must be a boolean value')
    .toBoolean()
];

// Exhibition ID parameter validation
const exhibitionIdValidation = [
  param('id')
    .isUUID()
    .withMessage('Exhibition ID must be a valid UUID')
];

// Exhibition interaction validation (likes, views)
const exhibitionInteractionValidation = [
  param('id')
    .isUUID()
    .withMessage('Exhibition ID must be a valid UUID'),

  body('action')
    .isIn(['like', 'unlike', 'view'])
    .withMessage('Action must be one of: like, unlike, view'),

  body('metadata')
    .optional()
    .isObject()
    .custom((value) => {
      // Validate metadata structure and size
      const stringified = JSON.stringify(value);
      if (stringified.length > 1000) {
        throw new Error('Metadata too large');
      }

      const allowedKeys = ['source', 'device', 'referrer', 'duration'];
      const keys = Object.keys(value);
      if (keys.some(key => !allowedKeys.includes(key))) {
        throw new Error('Invalid metadata keys');
      }

      return true;
    })
];

// Validation result handler with enhanced logging
const handleExhibitionValidationResult = (req, res, next) => {
  const errors = validationResult(req);

  if (!errors.isEmpty()) {
    const errorDetails = errors.array().map(error => ({
      field: error.path || error.param,
      message: error.msg,
      value: error.value,
      location: error.location
    }));

    // Log validation failures for security monitoring
    log.warn('Exhibition validation failed', {
      ip: req.ip,
      userAgent: req.headers['user-agent'],
      path: req.path,
      method: req.method,
      userId: req.userId,
      errors: errorDetails,
      body: sanitizeLogData(req.body),
      query: req.query,
      params: req.params
    });

    return res.status(400).json({
      error: 'Validation failed',
      code: 'EXHIBITION_VALIDATION_ERROR',
      details: errorDetails,
      timestamp: new Date().toISOString()
    });
  }

  next();
};

// Sanitize data for logging (remove sensitive info)
function sanitizeLogData(data) {
  if (!data || typeof data !== 'object') {
    return data;
  }

  const sensitiveFields = ['password', 'token', 'secret', 'key'];
  const sanitized = { ...data };

  sensitiveFields.forEach(field => {
    if (sanitized[field]) {
      sanitized[field] = '[REDACTED]';
    }
  });

  return sanitized;
}

// Input sanitization for exhibition data
const sanitizeExhibitionInput = (req, res, next) => {
  if (req.body) {
    // Sanitize string fields
    const stringFields = [
      'title', 'description', 'venue_name', 'venue_address',
      'venue_city', 'submitter_name', 'organization'
    ];

    stringFields.forEach(field => {
      if (req.body[field] && typeof req.body[field] === 'string') {
        // Remove HTML tags and normalize whitespace
        req.body[field] = req.body[field]
          .replace(/<[^>]*>/g, '') // Strip HTML tags
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();
      }
    });

    // Sanitize arrays (tags)
    if (req.body.tags && Array.isArray(req.body.tags)) {
      req.body.tags = req.body.tags
        .filter(tag => typeof tag === 'string')
        .map(tag => tag.replace(/<[^>]*>/g, '').trim())
        .filter(tag => tag.length > 0);
    }

    // Ensure numeric fields are properly typed
    const numericFields = ['admission_fee', 'latitude', 'longitude'];
    numericFields.forEach(field => {
      if (req.body[field] !== undefined && req.body[field] !== null) {
        const num = parseFloat(req.body[field]);
        if (!isNaN(num)) {
          req.body[field] = num;
        }
      }
    });
  }

  next();
};

// Rate limiting for exhibition submissions
const submissionRateLimit = require('express-rate-limit')({
  windowMs: 60 * 60 * 1000, // 1 hour
  max: 5, // 5 submissions per hour per IP
  message: {
    error: 'Too many exhibition submissions, please try again later',
    code: 'SUBMISSION_RATE_LIMIT_EXCEEDED',
    retryAfter: '1 hour'
  },
  standardHeaders: true,
  legacyHeaders: false,
  keyGenerator: (req) => {
    // Rate limit by IP + user ID if authenticated
    return req.userId ? `${req.ip}-${req.userId}` : req.ip;
  }
});

module.exports = {
  exhibitionValidation,
  exhibitionSubmissionValidation,
  exhibitionQueryValidation,
  exhibitionIdValidation,
  exhibitionInteractionValidation,
  handleExhibitionValidationResult,
  sanitizeExhibitionInput,
  submissionRateLimit
};

/**
 * SAYU Memory Management Middleware
 * Express ë¯¸ë“¤ì›¨ì–´ë¡œì„œ ìš”ì²­ë³„ ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ ëª¨ë‹ˆí„°ë§ ë° ìµœì í™”
 */

const MemoryOptimizer = require('../utils/memoryOptimizer');

class MemoryMiddleware {
  constructor(options = {}) {
    this.options = {
      // ìš”ì²­ë³„ ë©”ëª¨ë¦¬ ëª¨ë‹ˆí„°ë§
      enableRequestMonitoring: options.enableRequestMonitoring !== false,

      // ë©”ëª¨ë¦¬ ì„ê³„ê°’ (ìš”ì²­ ì²˜ë¦¬ ì¤‘)
      requestMemoryLimitMB: options.requestMemoryLimitMB || 100,

      // ëŠë¦° ìš”ì²­ ê°ì§€ (ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ê°€ëŠ¥ì„±)
      slowRequestThresholdMs: options.slowRequestThresholdMs || 5000,

      // ëŒ€ìš©ëŸ‰ ìš”ì²­ ì œí•œ
      largeRequestSizeMB: options.largeRequestSizeMB || 50,

      // ë™ì‹œ ìš”ì²­ ìˆ˜ ì œí•œ (ë©”ëª¨ë¦¬ ë³´í˜¸)
      maxConcurrentRequests: options.maxConcurrentRequests || 100,

      // ìë™ ì •ë¦¬ ì˜µì…˜
      enableAutoCleanup: options.enableAutoCleanup !== false,
      cleanupInterval: options.cleanupInterval || 60000, // 1ë¶„

      // ë¡œê¹…
      enableLogging: options.enableLogging !== false
    };

    // í†µê³„ ì •ë³´
    this.stats = {
      totalRequests: 0,
      activeRequests: 0,
      memoryLeakDetected: 0,
      slowRequests: 0,
      largeRequests: 0,
      cleanupCount: 0
    };

    // í™œì„± ìš”ì²­ ì¶”ì 
    this.activeRequestsMap = new Map();

    // ë©”ëª¨ë¦¬ ìµœì í™”ê¸° ì¸ìŠ¤í„´ìŠ¤
    this.memoryOptimizer = new MemoryOptimizer({
      maxMemoryMB: 2048,
      warningThresholdMB: 1536,
      enableAutoCleanup: true,
      enableLogging: this.options.enableLogging
    });

    // ìë™ ì •ë¦¬ íƒ€ì´ë¨¸ ì„¤ì •
    if (this.options.enableAutoCleanup) {
      this.setupAutoCleanup();
    }

    this.log('MemoryMiddleware initialized', 'info');
  }

  /**
   * Express ë¯¸ë“¤ì›¨ì–´ í•¨ìˆ˜
   */
  middleware() {
    return (req, res, next) => {
      const requestId = this.generateRequestId();
      const startTime = Date.now();
      const startMemory = process.memoryUsage();

      // ìš”ì²­ ì •ë³´ ì €ì¥
      const requestInfo = {
        id: requestId,
        method: req.method,
        url: req.url,
        startTime,
        startMemory,
        userAgent: req.get('User-Agent'),
        ip: req.ip || req.connection.remoteAddress
      };

      // ìš”ì²­ ì „ ê²€ì‚¬
      if (!this.preRequestCheck(req, res, requestInfo)) {
        return; // ìš”ì²­ì´ ê±°ë¶€ëœ ê²½ìš°
      }

      // í™œì„± ìš”ì²­ ë“±ë¡
      this.activeRequestsMap.set(requestId, requestInfo);
      this.stats.activeRequests++;
      this.stats.totalRequests++;

      // ìš”ì²­ í¬ê¸° ì²´í¬
      this.checkRequestSize(req, requestInfo);

      // ì‘ë‹µ ì¢…ë£Œ ì‹œ ì •ë¦¬
      const originalEnd = res.end;
      res.end = (...args) => {
        this.postRequestCleanup(requestId, requestInfo, startTime, startMemory);
        originalEnd.apply(res, args);
      };

      // ì—ëŸ¬ ì²˜ë¦¬
      res.on('error', (error) => {
        this.handleRequestError(requestId, requestInfo, error);
      });

      // ìš”ì²­ íƒ€ì„ì•„ì›ƒ ê°ì§€
      const timeoutTimer = setTimeout(() => {
        this.handleSlowRequest(requestId, requestInfo);
      }, this.options.slowRequestThresholdMs);

      requestInfo.timeoutTimer = timeoutTimer;

      next();
    };
  }

  /**
   * ìš”ì²­ ì „ ê²€ì‚¬
   */
  preRequestCheck(req, res, requestInfo) {
    // ë™ì‹œ ìš”ì²­ ìˆ˜ ì œí•œ í™•ì¸
    if (this.stats.activeRequests >= this.options.maxConcurrentRequests) {
      this.log(`Too many concurrent requests: ${this.stats.activeRequests}`, 'warn');

      res.status(503).json({
        error: 'Service temporarily unavailable',
        message: 'Too many concurrent requests'
      });

      return false;
    }

    // í˜„ì¬ ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ í™•ì¸
    const currentMemory = process.memoryUsage();
    const heapUsedMB = Math.round(currentMemory.heapUsed / 1024 / 1024);

    if (heapUsedMB > this.memoryOptimizer.options.warningThresholdMB) {
      this.log(`High memory usage detected: ${heapUsedMB}MB`, 'warn');

      // ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ì´ ë†’ì€ ê²½ìš° ì¼ë¶€ ìš”ì²­ ê±°ë¶€
      if (heapUsedMB > this.memoryOptimizer.options.maxMemoryMB * 0.9) {
        res.status(503).json({
          error: 'Service temporarily unavailable',
          message: 'High memory usage detected'
        });

        return false;
      }
    }

    return true;
  }

  /**
   * ìš”ì²­ í¬ê¸° ì²´í¬
   */
  checkRequestSize(req, requestInfo) {
    const contentLength = parseInt(req.get('content-length') || '0');
    const requestSizeMB = contentLength / 1024 / 1024;

    if (requestSizeMB > this.options.largeRequestSizeMB) {
      this.log(`Large request detected: ${requestSizeMB.toFixed(2)}MB from ${requestInfo.ip}`, 'warn');
      this.stats.largeRequests++;
      requestInfo.isLargeRequest = true;

      // ëŒ€ìš©ëŸ‰ ìš”ì²­ì„ ìœ„í•œ ë©”ëª¨ë¦¬ ì‚¬ì „ ì •ë¦¬
      if (this.options.enableAutoCleanup) {
        this.memoryOptimizer.performCleanup('normal');
      }
    }
  }

  /**
   * ëŠë¦° ìš”ì²­ ì²˜ë¦¬
   */
  handleSlowRequest(requestId, requestInfo) {
    const duration = Date.now() - requestInfo.startTime;
    this.log(`Slow request detected: ${requestInfo.method} ${requestInfo.url} - ${duration}ms`, 'warn');

    this.stats.slowRequests++;
    requestInfo.isSlowRequest = true;

    // ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ê°€ëŠ¥ì„± ì²´í¬
    const currentMemory = process.memoryUsage();
    const memoryIncrease = currentMemory.heapUsed - requestInfo.startMemory.heapUsed;
    const memoryIncreaseMB = Math.round(memoryIncrease / 1024 / 1024);

    if (memoryIncreaseMB > this.options.requestMemoryLimitMB) {
      this.log(`Potential memory leak in request ${requestId}: ${memoryIncreaseMB}MB increase`, 'error');
      this.stats.memoryLeakDetected++;

      // ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ê°ì§€ ì‹œ ê°•ì œ ì •ë¦¬
      this.memoryOptimizer.forceGarbageCollection();
    }
  }

  /**
   * ìš”ì²­ ì—ëŸ¬ ì²˜ë¦¬
   */
  handleRequestError(requestId, requestInfo, error) {
    this.log(`Request error ${requestId}: ${error.message}`, 'error');

    // ë©”ëª¨ë¦¬ ê´€ë ¨ ì—ëŸ¬ ì²´í¬
    if (error.message.includes('out of memory') || error.code === 'ENOMEM') {
      this.log('Memory error detected in request', 'error');
      this.memoryOptimizer.performEmergencyCleanup();
    }
  }

  /**
   * ìš”ì²­ í›„ ì •ë¦¬
   */
  postRequestCleanup(requestId, requestInfo, startTime, startMemory) {
    const endTime = Date.now();
    const endMemory = process.memoryUsage();
    const duration = endTime - startTime;

    // íƒ€ì„ì•„ì›ƒ íƒ€ì´ë¨¸ ì •ë¦¬
    if (requestInfo.timeoutTimer) {
      clearTimeout(requestInfo.timeoutTimer);
    }

    // ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ ê³„ì‚°
    const memoryDiff = {
      rss: endMemory.rss - startMemory.rss,
      heapUsed: endMemory.heapUsed - startMemory.heapUsed,
      heapTotal: endMemory.heapTotal - startMemory.heapTotal,
      external: endMemory.external - startMemory.external
    };

    const memoryIncreaseMB = Math.round(memoryDiff.heapUsed / 1024 / 1024);

    // ìš”ì²­ í†µê³„ ì—…ë°ì´íŠ¸
    this.updateRequestStats(requestInfo, duration, memoryIncreaseMB);

    // í™œì„± ìš”ì²­ì—ì„œ ì œê±°
    this.activeRequestsMap.delete(requestId);
    this.stats.activeRequests--;

    // ë¡œê¹… (ê°œë°œ í™˜ê²½ì—ì„œë§Œ)
    if (process.env.NODE_ENV === 'development' && this.options.enableLogging) {
      this.logRequestDetails(requestInfo, duration, memoryIncreaseMB);
    }

    // ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ì²´í¬
    if (memoryIncreaseMB > this.options.requestMemoryLimitMB) {
      this.handlePotentialMemoryLeak(requestId, requestInfo, memoryIncreaseMB);
    }

    // ì£¼ê¸°ì  ì •ë¦¬ (100ë²ˆì§¸ ìš”ì²­ë§ˆë‹¤)
    if (this.stats.totalRequests % 100 === 0) {
      this.performPeriodicCleanup();
    }
  }

  /**
   * ìš”ì²­ í†µê³„ ì—…ë°ì´íŠ¸
   */
  updateRequestStats(requestInfo, duration, memoryIncrease) {
    // í†µê³„ ì •ë³´ëŠ” ë©”ëª¨ë¦¬ì— ëˆ„ì ë˜ì§€ ì•Šë„ë¡ ì œí•œ
    if (!this.requestStats) {
      this.requestStats = {
        avgDuration: 0,
        avgMemoryIncrease: 0,
        count: 0
      };
    }

    // ì´ë™ í‰ê·  ê³„ì‚°
    this.requestStats.count++;
    this.requestStats.avgDuration =
      (this.requestStats.avgDuration * (this.requestStats.count - 1) + duration) / this.requestStats.count;
    this.requestStats.avgMemoryIncrease =
      (this.requestStats.avgMemoryIncrease * (this.requestStats.count - 1) + memoryIncrease) / this.requestStats.count;

    // í†µê³„ í¬ê¸° ì œí•œ (ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ë°©ì§€)
    if (this.requestStats.count > 1000) {
      this.requestStats.count = 500; // ì ˆë°˜ìœ¼ë¡œ ë¦¬ì…‹
    }
  }

  /**
   * ìš”ì²­ ì„¸ë¶€ì‚¬í•­ ë¡œê¹…
   */
  logRequestDetails(requestInfo, duration, memoryIncrease) {
    const logLevel = memoryIncrease > 10 ? 'warn' : 'debug';

    this.log(
      `Request ${requestInfo.id}: ${requestInfo.method} ${requestInfo.url} ` +
      `- Duration: ${duration}ms, Memory: ${memoryIncrease}MB`,
      logLevel
    );
  }

  /**
   * ì ì¬ì  ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ì²˜ë¦¬
   */
  handlePotentialMemoryLeak(requestId, requestInfo, memoryIncrease) {
    this.log(
      `Potential memory leak detected in ${requestInfo.method} ${requestInfo.url}: ${memoryIncrease}MB`,
      'error'
    );

    this.stats.memoryLeakDetected++;

    // ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ íŒ¨í„´ ë¶„ì„
    const leakPattern = this.analyzeMemoryLeakPattern(requestInfo);

    if (leakPattern.isLeak) {
      this.log(`Memory leak pattern confirmed: ${leakPattern.reason}`, 'error');

      // ê°•ì œ ê°€ë¹„ì§€ ì»¬ë ‰ì…˜
      this.memoryOptimizer.forceGarbageCollection();

      // ëˆ„ìˆ˜ ì•Œë¦¼
      this.memoryOptimizer.emit('memory:leak', {
        requestId,
        requestInfo,
        memoryIncrease,
        pattern: leakPattern
      });
    }
  }

  /**
   * ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ íŒ¨í„´ ë¶„ì„
   */
  analyzeMemoryLeakPattern(requestInfo) {
    // ë‹¨ìˆœí•œ íŒ¨í„´ ë¶„ì„ (ì‹¤ì œë¡œëŠ” ë” ë³µì¡í•œ ë¶„ì„ í•„ìš”)
    const reasons = [];

    // ëŒ€ìš©ëŸ‰ ìš”ì²­
    if (requestInfo.isLargeRequest) {
      reasons.push('Large request body');
    }

    // ëŠë¦° ìš”ì²­
    if (requestInfo.isSlowRequest) {
      reasons.push('Slow request processing');
    }

    // íŠ¹ì • ê²½ë¡œ íŒ¨í„´
    const leakyPaths = ['/api/upload', '/api/process-image', '/api/export'];
    if (leakyPaths.some(path => requestInfo.url.includes(path))) {
      reasons.push('Known memory-intensive endpoint');
    }

    return {
      isLeak: reasons.length > 1,
      reason: reasons.join(', '),
      confidence: reasons.length * 0.3
    };
  }

  /**
   * ì£¼ê¸°ì  ì •ë¦¬
   */
  performPeriodicCleanup() {
    this.log('Performing periodic cleanup', 'debug');

    try {
      // ì˜¤ë˜ëœ ìš”ì²­ ì •ë³´ ì •ë¦¬
      this.cleanupOldRequests();

      // í†µê³„ ì •ë¦¬
      this.cleanupStats();

      // ë©”ëª¨ë¦¬ ìµœì í™”ê¸° ì •ë¦¬ ì‹¤í–‰
      if (this.options.enableAutoCleanup) {
        this.memoryOptimizer.performCleanup('normal');
      }

      this.stats.cleanupCount++;

    } catch (error) {
      this.log(`Periodic cleanup failed: ${error.message}`, 'error');
    }
  }

  /**
   * ì˜¤ë˜ëœ ìš”ì²­ ì •ë³´ ì •ë¦¬
   */
  cleanupOldRequests() {
    const now = Date.now();
    const maxAge = 300000; // 5ë¶„

    let cleanedCount = 0;

    for (const [requestId, requestInfo] of this.activeRequestsMap.entries()) {
      if (now - requestInfo.startTime > maxAge) {
        // íƒ€ì„ì•„ì›ƒ íƒ€ì´ë¨¸ ì •ë¦¬
        if (requestInfo.timeoutTimer) {
          clearTimeout(requestInfo.timeoutTimer);
        }

        this.activeRequestsMap.delete(requestId);
        this.stats.activeRequests--;
        cleanedCount++;
      }
    }

    if (cleanedCount > 0) {
      this.log(`Cleaned up ${cleanedCount} old requests`, 'debug');
    }
  }

  /**
   * í†µê³„ ì •ë¦¬
   */
  cleanupStats() {
    // í†µê³„ê°€ ë„ˆë¬´ ì»¤ì§€ë©´ ë¦¬ì…‹
    if (this.stats.totalRequests > 100000) {
      this.stats.totalRequests = 50000;
      this.stats.slowRequests = Math.floor(this.stats.slowRequests / 2);
      this.stats.largeRequests = Math.floor(this.stats.largeRequests / 2);
      this.stats.memoryLeakDetected = Math.floor(this.stats.memoryLeakDetected / 2);

      this.log('Statistics reset to prevent memory growth', 'debug');
    }
  }

  /**
   * ìë™ ì •ë¦¬ ì„¤ì •
   */
  setupAutoCleanup() {
    this.cleanupTimer = setInterval(() => {
      this.performPeriodicCleanup();
    }, this.options.cleanupInterval);

    this.log(`Auto cleanup scheduled every ${this.options.cleanupInterval / 1000}s`, 'debug');
  }

  /**
   * í†µê³„ ì •ë³´ ë°˜í™˜
   */
  getStats() {
    const memoryUsage = process.memoryUsage();

    return {
      requests: this.stats,
      memory: {
        rss: Math.round(memoryUsage.rss / 1024 / 1024),
        heapUsed: Math.round(memoryUsage.heapUsed / 1024 / 1024),
        heapTotal: Math.round(memoryUsage.heapTotal / 1024 / 1024),
        external: Math.round(memoryUsage.external / 1024 / 1024)
      },
      activeRequests: this.activeRequestsMap.size,
      uptime: Math.round(process.uptime()),
      averages: this.requestStats || null
    };
  }

  /**
   * ìš”ì²­ ID ìƒì„±
   */
  generateRequestId() {
    return `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * ì •ë¦¬
   */
  cleanup() {
    this.log('Cleaning up memory middleware', 'info');

    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
    }

    // ëª¨ë“  í™œì„± ìš”ì²­ì˜ íƒ€ì´ë¨¸ ì •ë¦¬
    for (const [requestId, requestInfo] of this.activeRequestsMap.entries()) {
      if (requestInfo.timeoutTimer) {
        clearTimeout(requestInfo.timeoutTimer);
      }
    }

    this.activeRequestsMap.clear();

    if (this.memoryOptimizer) {
      this.memoryOptimizer.cleanup();
    }
  }

  /**
   * ë¡œê¹…
   */
  log(message, level = 'info') {
    if (!this.options.enableLogging) return;

    const timestamp = new Date().toISOString();
    const logMessage = `[${timestamp}] [MemoryMiddleware] [${level.toUpperCase()}] ${message}`;

    if (console[level]) {
      console[level](logMessage);
    } else {
      console.log(logMessage);
    }
  }
}

