    const deltaVector = oldVector.map((v, i) => newVector[i] - v);
    const changeMagnitude = Math.sqrt(deltaVector.reduce((sum, d) => sum + d * d, 0));

    if (changeMagnitude > 0.1) {
      insights.changes.push('ì·¨í–¥ì´ í™•ì¥ë˜ê³  ìˆì–´ìš”! ìƒˆë¡œìš´ ìŠ¤íƒ€ì¼ì— ë§ˆìŒì„ ì—´ê³  ê³„ì‹œë„¤ìš”.');
    } else if (changeMagnitude > 0.05) {
      insights.changes.push('ì¡°ê¸ˆì”© ì·¨í–¥ì´ ì§„í™”í•˜ê³  ìˆì–´ìš”. ê¾¸ì¤€í•œ íƒí—˜ì´ ë‹ë³´ì…ë‹ˆë‹¤.');
    }

    // APTë³„ ì„±ì¥ ì˜ì—­ ì œì•ˆ
    const typeData = SAYU_TYPES[aptType];
    if (aptType[0] === 'L') {
      insights.growthAreas.push('ê°€ë”ì€ ì¹œêµ¬ì™€ í•¨ê»˜ ì „ì‹œë¥¼ ê´€ëŒí•´ë³´ëŠ” ê²ƒë„ ì¢‹ì„ ê±°ì˜ˆìš”.');
    }
    if (aptType[1] === 'A') {
      insights.growthAreas.push('êµ¬ìƒ ì‘í’ˆë„ í•œë²ˆ ë“¤ì—¬ë‹¤ë³´ë©´ ìƒˆë¡œìš´ ë§¤ë ¥ì„ ë°œê²¬í•  ìˆ˜ ìˆì–´ìš”.');
    }

