  // ==================== ì¹œí™”ë„ ì‹œìŠ¤í…œ ====================

  buildAffinityMatrix() {
    const matrix = {};

    for (const type1 of Object.keys(SAYU_TYPES)) {
      matrix[type1] = {};
      for (const type2 of Object.keys(SAYU_TYPES)) {
        matrix[type1][type2] = this.calculateAffinity(type1, type2);
      }
    }

    return matrix;
  }

  calculateAffinity(type1, type2) {
    if (type1 === type2) return 1.0; // ê°™ì€ íƒ€ì…ì€ 100% ì¹œí™”ë„

    let affinity = 0;

    // ê°™ì€ ì¶•ì„ ê³µìœ í•  ë•Œë§ˆë‹¤ ì¹œí™”ë„ ì¦ê°€
    for (let i = 0; i < 4; i++) {
      if (type1[i] === type2[i]) {
