    return affinity;
  }

  getRecommendedConnections(aptType) {
    // ì¹œí™”ë„ê°€ ë†’ì€ ë‹¤ë¥¸ APT ìœ í˜• ì¶”ì²œ
    const affinities = this.affinityMatrix[aptType];
    return Object.entries(affinities)
      .filter(([type, _]) => type !== aptType)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3)
      .map(([type, affinity]) => ({
