// ì…ë ¥ ê²€ì¦ ë° sanitization ë¯¸ë“¤ì›¨ì–´
const validateAndSanitize = (req, res, next) => {
  const errors = validationResult(req);
  if (!errors.isEmpty()) {
    logger.warn('Validation failed:', {
      errors: errors.array(),
      ip: req.ip,
      userAgent: req.get('User-Agent')
    });
    return res.status(400).json({
      success: false,
      message: 'Invalid input data',
      errors: errors.array()
    });
  }

  // ëª¨ë“  ë¬¸ìì—´ í•„ë“œ sanitize
  const sanitizeObject = (obj) => {
    if (typeof obj === 'string') {
      return DOMPurify.sanitize(obj, { ALLOWED_TAGS: [] }); // ëª¨ë“  HTML íƒœê·¸ ì œê±°
    }
    if (Array.isArray(obj)) {
      return obj.map(sanitizeObject);
    }
    if (obj && typeof obj === 'object') {
      const sanitized = {};
      for (const [key, value] of Object.entries(obj)) {
        sanitized[key] = sanitizeObject(value);
      }
      return sanitized;
    }
    return obj;
  };

  req.body = sanitizeObject(req.body);
  next();
};

// ì•…ì„± íŒŒì¼ ê²€ì‚¬ ë¯¸ë“¤ì›¨ì–´
const scanFile = async (req, res, next) => {
  if (!req.files || req.files.length === 0) {
    return next();
  }

  try {
    for (const file of req.files) {
      // íŒŒì¼ ì‹œê·¸ë‹ˆì²˜ ê²€ì‚¬ (ë§¤ì§ ë„˜ë²„)
      const { buffer } = file;

      // JPEG ì‹œê·¸ë‹ˆì²˜: FF D8 FF
      // PNG ì‹œê·¸ë‹ˆì²˜: 89 50 4E 47
      // GIF ì‹œê·¸ë‹ˆì²˜: 47 49 46 38
      // WebP ì‹œê·¸ë‹ˆì²˜: 52 49 46 46 (RIFF) + 57 45 42 50 (WEBP)

      const isValidImage = (
        (buffer[0] === 0xFF && buffer[1] === 0xD8 && buffer[2] === 0xFF) || // JPEG
        (buffer[0] === 0x89 && buffer[1] === 0x50 && buffer[2] === 0x4E && buffer[3] === 0x47) || // PNG
        (buffer[0] === 0x47 && buffer[1] === 0x49 && buffer[2] === 0x46 && buffer[3] === 0x38) || // GIF
        (buffer[0] === 0x52 && buffer[1] === 0x49 && buffer[2] === 0x46 && buffer[3] === 0x46 &&
         buffer[8] === 0x57 && buffer[9] === 0x45 && buffer[10] === 0x42 && buffer[11] === 0x50) // WebP
      );

      if (!isValidImage) {
        logger.warn('Invalid file signature detected:', {
          filename: file.originalname,
          mimetype: file.mimetype,
          ip: req.ip
        });
        return res.status(400).json({
          success: false,
          message: 'Invalid file format detected'
        });
      }
    }
    next();
  } catch (error) {
    logger.error('File scan error:', error);
    res.status(500).json({
      success: false,
      message: 'File validation failed'
    });
  }
};

