router.post('/admin/security/ip/:ip/reputation', requireAdmin,
  [
    param('ip').isIP().withMessage('Valid IP address required'),
    body('action').isIn(['block', 'unblock', 'reset']).withMessage('Action must be block, unblock, or reset'),
    body('reason').optional().isLength({ max: 200 }).trim()
  ],
  validateAndSanitize,
  async (req, res) => {
    try {
      const { ip } = req.params;
      const { action, reason } = req.body;
      const redis = getRedisClient();

      if (!redis) {
        return res.status(503).json({ error: 'Redis not available' });
      }

      const key = `ip_reputation:${ip}`;
      let newReputation;

      switch (action) {
        case 'block':
          newReputation = -100;
          await redis.set(key, newReputation, 'EX', 7 * 24 * 60 * 60); // 7ì¼ê°„ ì°¨ë‹¨
          break;
        case 'unblock':
          newReputation = 0;
          await redis.set(key, newReputation, 'EX', 24 * 60 * 60); // 1ì¼ê°„ ì¤‘ë¦½
          break;
        case 'reset':
          await redis.del(key);
          newReputation = null;
          break;
      }

      // ê´€ë¦¬ì ì•¡ì…˜ ë¡œê¹…
      logger.info('Admin IP reputation change:', {
        adminId: req.userId,
        targetIP: ip,
        action,
        reason,
        newReputation,
        timestamp: new Date().toISOString()
      });

      res.json({
        success: true,
        ip,
        action,
        newReputation,
        message: `IP ${ip} has been ${action}ed`
      });
    } catch (error) {
      logger.error('Failed to manage IP reputation:', error);
      res.status(500).json({ error: 'Failed to manage IP reputation' });
    }
  }
);

