const router = require('express').Router();
const authMiddleware = require('../middleware/auth');
const { pool } = require('../config/database');
const { redisClient } = require('../config/redis');
const { logger } = require('../config/logger');
const {
  validationSchemas,
  handleValidationResult,
  securityHeaders,
  requestSizeLimiter,
  sanitizeInput,
  rateLimits
} = require('../middleware/validation');
const { query, param } = require('express-validator');

// Apply security middleware
router.use(securityHeaders);
router.use(sanitizeInput);
router.use(requestSizeLimiter('2mb'));

// Validation schemas
const artistListValidation = [
  query('page').optional().isInt({ min: 1 }).withMessage('Page must be a positive integer'),
  query('limit').optional().isInt({ min: 1, max: 100 }).withMessage('Limit must be between 1 and 100'),
  query('copyrightStatus').optional().isIn(['public_domain', 'licensed', 'contemporary', 'verified_artist']),
  query('nationality').optional().isString().trim().isLength({ min: 1, max: 50 }),
  query('era').optional().isString().trim().isLength({ min: 1, max: 50 }),
  query('search').optional().isString().trim().isLength({ min: 1, max: 100 }),
  query('sortBy').optional().isIn(['name', 'popularity', 'birth_year', 'follow_count']),
  query('sortOrder').optional().isIn(['asc', 'desc'])
];

const artistIdValidation = [
  param('id').isUUID().withMessage('Artist ID must be a valid UUID')
];

// Get paginated list of artists
router.get('/',
  rateLimits.lenient,
  artistListValidation,
  handleValidationResult,
  async (req, res) => {
    try {
      const {
        page = 1,
        limit = 20,
        copyrightStatus,
        nationality,
        era,
        search,
        sortBy = 'name',
        sortOrder = 'asc'
      } = req.query;

      const offset = (parseInt(page) - 1) * parseInt(limit);

      // Build WHERE clause
      const whereConditions = [];
      const queryParams = [];
      let paramIndex = 1;

      if (copyrightStatus) {
        whereConditions.push(`copyright_status = $${paramIndex++}`);
        queryParams.push(copyrightStatus);
      }

      if (nationality) {
        whereConditions.push(`nationality ILIKE $${paramIndex++}`);
        queryParams.push(`%${nationality}%`);
      }

      if (era) {
        whereConditions.push(`era ILIKE $${paramIndex++}`);
        queryParams.push(`%${era}%`);
      }

      if (search) {
        whereConditions.push(`(name ILIKE $${paramIndex++} OR name_ko ILIKE $${paramIndex++} OR bio ILIKE $${paramIndex++})`);
        queryParams.push(`%${search}%`, `%${search}%`, `%${search}%`);
        paramIndex += 2; // We added 2 more params above
      }

      const whereClause = whereConditions.length > 0 ? `WHERE ${whereConditions.join(' AND ')}` : '';

      // Build ORDER BY clause
      const orderByMap = {
        name: 'name',
        popularity: 'follow_count',
        birth_year: 'birth_year',
        follow_count: 'follow_count'
      };

      const orderBy = `ORDER BY ${orderByMap[sortBy] || 'name'} ${sortOrder.toUpperCase()}`;

      // Main query
      const mainQuery = `
        SELECT 
          id,
          name,
          name_ko,
          birth_year,
          death_year,
          nationality,
          nationality_ko,
          bio,
          bio_ko,
          copyright_status,
          follow_count,
          created_at,
          updated_at,
          images,
          sources,
          license_info,
          official_links,
          representation,
          recent_exhibitions,
          media_links,
          is_verified,
          verification_date,
          verification_method,
          artist_managed,
          permissions,
          purchase_links
        FROM artists
        ${whereClause}
        ${orderBy}
        LIMIT $${paramIndex++} OFFSET $${paramIndex++}
      `;

      // Count query
      const countQuery = `
        SELECT COUNT(*) as total
        FROM artists
        ${whereClause}
      `;

      queryParams.push(parseInt(limit), offset);
      const countParams = queryParams.slice(0, -2); // Remove limit and offset for count query

      const [artistsResult, countResult] = await Promise.all([
        pool.query(mainQuery, queryParams),
        pool.query(countQuery, countParams)
      ]);

      const artists = artistsResult.rows;
      const total = parseInt(countResult.rows[0].total);
      const totalPages = Math.ceil(total / parseInt(limit));

      res.json({
        artists,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total,
          totalPages,
          hasNext: parseInt(page) < totalPages,
          hasPrev: parseInt(page) > 1
        }
      });

    } catch (error) {
      logger.error('Get artists error:', error);
      res.status(500).json({ error: 'Failed to fetch artists' });
    }
  }
);

// Get single artist by ID
router.get('/:id',
  rateLimits.lenient,
  artistIdValidation,
  handleValidationResult,
  async (req, res) => {
    try {
      const { id } = req.params;

      // Check cache first
      const cacheKey = `artist:${id}`;
      const cached = await redisClient.get(cacheKey);
      if (cached) {
        return res.json(JSON.parse(cached));
      }

      const query = `
        SELECT 
          id,
          name,
          name_ko,
          birth_year,
          death_year,
          nationality,
          nationality_ko,
          bio,
          bio_ko,
          copyright_status,
          follow_count,
          created_at,
          updated_at,
          images,
          sources,
          license_info,
          official_links,
          representation,
          recent_exhibitions,
          media_links,
          is_verified,
          verification_date,
          verification_method,
          artist_managed,
          permissions,
          purchase_links
        FROM artists
        WHERE id = $1
      `;

      const result = await pool.query(query, [id]);

      if (result.rows.length === 0) {
        return res.status(404).json({ error: 'Artist not found' });
      }

      const artist = result.rows[0];

      // Cache for 1 hour
      await redisClient.setEx(cacheKey, 3600, JSON.stringify(artist));

      res.json(artist);

    } catch (error) {
      logger.error('Get artist error:', error);
      res.status(500).json({ error: 'Failed to fetch artist' });
    }
