    // í”„ë¡œí•„ ì—…ë°ì´íŠ¸
    await client.query(
      `UPDATE sayu_profiles 
      SET evolution_stage = $2, last_evolution_at = NOW()
      WHERE user_id = $1`,
      [userId, toStage]
    );

    // ì§„í™” ë§ˆì¼ìŠ¤í†¤ ë‹¬ì„±
    await client.query(
