  /**
   * ì‚¬ìš©ìì˜ ì›”ê°„ í¬ë ˆë”§ í™•ì¸
   */
  async checkUserCredits(userId) {
    try {
      const query = `
        SELECT 
          u.is_premium,
          COUNT(ap.id) as used_this_month
        FROM users u
        LEFT JOIN art_profiles ap ON u.id = ap.user_id
          AND ap.created_at >= date_trunc('month', CURRENT_DATE)
          AND ap.created_at < date_trunc('month', CURRENT_DATE) + interval '1 month'
        WHERE u.id = $1
        GROUP BY u.id, u.is_premium
      `;

      const result = await hybridDB.query(query, [userId]);
      const user = result.rows[0];

      if (!user) {
        throw new Error('User not found');
      }

      const monthlyLimit = user.is_premium
        ? parseInt(process.env.ART_PROFILE_PREMIUM_MONTHLY_LIMIT || '30')
        : parseInt(process.env.ART_PROFILE_FREE_MONTHLY_LIMIT || '3');

      return {
        isPremium: user.is_premium,
        used: parseInt(user.used_this_month || 0),
        remaining: monthlyLimit - parseInt(user.used_this_month || 0),
        limit: monthlyLimit
      };
    } catch (error) {
      logger.error('Error checking user credits:', error);
      throw error;
    }
  }

  /**
   * ì´ë¯¸ì§€ë¥¼ base64ì—ì„œ ë²„í¼ë¡œ ë³€í™˜í•˜ê³  ìµœì í™”
   */
  async processImage(base64Image) {
    try {
      // base64 ë°ì´í„° URIì—ì„œ ì‹¤ì œ base64 ë°ì´í„°ë§Œ ì¶”ì¶œ
      const base64Data = base64Image.replace(/^data:image\/\w+;base64,/, '');
      const imageBuffer = Buffer.from(base64Data, 'base64');

      // Sharpë¡œ ì´ë¯¸ì§€ ìµœì í™” (512x512 ë¦¬ì‚¬ì´ì¦ˆ)
      const optimizedBuffer = await sharp(imageBuffer)
        .resize(512, 512, {
          fit: 'cover',
          position: 'center'
        })
        .jpeg({ quality: 85 })
        .toBuffer();

      // ì´ë¯¸ì§€ í•´ì‹œ ìƒì„± (ìºì‹±ìš©)
      const imageHash = crypto
        .createHash('md5')
        .update(optimizedBuffer)
        .digest('hex');

      return { optimizedBuffer, imageHash };
    } catch (error) {
      logger.error('Error processing image:', error);
      throw new Error('Failed to process image');
    }
  }

  /**
   * Cloudinaryì— ì´ë¯¸ì§€ ì—…ë¡œë“œ
   */
  async uploadToCloudinary(buffer, folder) {
    return new Promise((resolve, reject) => {
      const uploadStream = cloudinary.uploader.upload_stream(
        {
          folder: `art-profiles/${folder}`,
          resource_type: 'image',
          format: 'jpg'
        },
        (error, result) => {
          if (error) reject(error);
          else resolve(result.secure_url);
        }
      );

      uploadStream.end(buffer);
    });
  }

  /**
   * ì¬ì‹œë„ ë¡œì§ì´ í¬í•¨ëœ Replicate API í˜¸ì¶œ
   */
  async generateWithRetry(styleConfig, originalImageUrl, styleId, customSettings = {}, maxRetries = 3) {
    let lastError;
    const startTime = Date.now();

    for (let attempt = 1; attempt <= maxRetries; attempt++) {
      try {
        const attemptStartTime = Date.now();
        logger.info(`Attempt ${attempt}/${maxRetries} for style: ${styleId}`);

        let output;
        if (styleId === 'pixel-art') {
          // í”½ì…€ ì•„íŠ¸ëŠ” ë‹¤ë¥¸ ëª¨ë¸ ì‚¬ìš©
          output = await this.replicate.run(
            styleConfig.model,
            {
              input: {
                prompts: styleConfig.prompt,
                ...styleConfig.settings
              }
            }
          );
        } else {
          // SDXL ëª¨ë¸ ì‚¬ìš© (NSFW í•„í„° ìš°íšŒë¥¼ ìœ„í•œ ì•ˆì „í•œ í”„ë¡¬í”„íŠ¸)
          const safePrompt = this.getSafePrompt(styleConfig.prompt, attempt);

          output = await this.replicate.run(
            styleConfig.model,
            {
              input: {
                prompt: safePrompt,
                negative_prompt: `${styleConfig.negative_prompt}, nsfw, explicit, adult, inappropriate`,
                image: originalImageUrl,
                prompt_strength: 0.8,
                num_outputs: 1,
                guidance_scale: 7.5,
                scheduler: 'K_EULER',
                num_inference_steps: 50,
                seed: Math.floor(Math.random() * 1000000), // ë‹¤ì–‘ì„±ì„ ìœ„í•œ ëœë¤ ì‹œë“œ
                ...customSettings
              }
            }
          );
        }

        // ì„±ê³µ ì‹œ ê²°ê³¼ ê²€ì¦
        if (output && Array.isArray(output) && output.length > 0 && output[0]) {
          const attemptDuration = Date.now() - attemptStartTime;
          const totalDuration = Date.now() - startTime;

          // ì„±ëŠ¥ ë©”íŠ¸ë¦­ ë¡œê¹…
          logger.info(`Successfully generated art profile on attempt ${attempt}`, {
            styleId,
            attempt,
            attemptDuration,
            totalDuration,
            success: true
          });

          // ì„±ëŠ¥ ë°ì´í„° ìºì‹± (ëª¨ë‹ˆí„°ë§ìš©)
          await this.logPerformanceMetrics(styleId, {
            attempts: attempt,
            totalDuration,
            success: true
          });

          return output;
        } else {
          throw new Error('Invalid output format from Replicate API');
        }

      } catch (error) {
        lastError = error;
        logger.warn(`Attempt ${attempt} failed for style ${styleId}: ${error.message}`);

        // NSFW ì—ëŸ¬ì¸ ê²½ìš° ë‹¤ë¥¸ ì‹œë“œë¡œ ì¬ì‹œë„
        if (error.message.includes('NSFW') || error.message.includes('content detected')) {
          logger.info(`NSFW detected, retrying with different seed...`);
          continue;
        }

        // ë‹¤ë¥¸ ì—ëŸ¬ì¸ ê²½ìš° ì ì‹œ ëŒ€ê¸° í›„ ì¬ì‹œë„
        if (attempt < maxRetries) {
          const delay = Math.pow(2, attempt) * 1000; // ì§€ìˆ˜ ë°±ì˜¤í”„
          logger.info(`Waiting ${delay}ms before retry...`);
          await new Promise(resolve => setTimeout(resolve, delay));
        }
      }
    }

    // ëª¨ë“  ì¬ì‹œë„ ì‹¤íŒ¨
    const totalDuration = Date.now() - startTime;

    // ì‹¤íŒ¨ ë©”íŠ¸ë¦­ ë¡œê¹…
    await this.logPerformanceMetrics(styleId, {
      attempts: maxRetries,
      totalDuration,
      success: false,
      error: lastError.message
    });

    logger.error(`All ${maxRetries} attempts failed for style ${styleId}`, {
      styleId,
      maxRetries,
      totalDuration,
      lastError: lastError.message
    });

    throw new Error(`Failed to generate art profile after ${maxRetries} attempts: ${lastError.message}`);
  }

  /**
   * NSFW í•„í„° ìš°íšŒë¥¼ ìœ„í•œ ì•ˆì „í•œ í”„ë¡¬í”„íŠ¸ ìƒì„±
   */
  getSafePrompt(originalPrompt, attempt) {
    const safeWords = [
      'artistic portrait',
      'professional headshot',
      'creative artwork',
      'stylized illustration',
      'digital art piece'
    ];

    const safeWord = safeWords[attempt % safeWords.length];

    return `${originalPrompt}, ${safeWord}, clean, appropriate, family-friendly`;
  }

  /**
   * ì„±ëŠ¥ ë©”íŠ¸ë¦­ ë¡œê¹… (ëª¨ë‹ˆí„°ë§ìš©)
   */
  async logPerformanceMetrics(styleId, metrics) {
    try {
      const key = `performance:${styleId}:${new Date().toISOString().split('T')[0]}`;
      const data = {
        timestamp: new Date().toISOString(),
        styleId,
        ...metrics
      };

      // Redisì— ì„±ëŠ¥ ë°ì´í„° ì €ì¥ (24ì‹œê°„ TTL)
      await redis.lpush(key, JSON.stringify(data));
      await redis.expire(key, 86400); // 24ì‹œê°„

      // ì¼ì¼ í†µê³„ ì—…ë°ì´íŠ¸
      const statsKey = `stats:art-profile:${new Date().toISOString().split('T')[0]}`;
      await redis.hincrby(statsKey, `${styleId}:attempts`, metrics.attempts);
      await redis.hincrby(statsKey, `${styleId}:${metrics.success ? 'success' : 'failure'}`, 1);
      await redis.expire(statsKey, 86400 * 7); // 7ì¼ê°„ ë³´ê´€

    } catch (error) {
      logger.warn('Failed to log performance metrics:', error.message);
    }
  }

  /**
   * AI ì•„íŠ¸ í”„ë¡œí•„ ìƒì„±
   */
  async generateArtProfile(userId, base64Image, styleId, customSettings = {}) {
    try {
      // 1. í¬ë ˆë”§ í™•ì¸
      const credits = await this.checkUserCredits(userId);
      if (credits.remaining <= 0) {
        throw new Error('No credits remaining this month');
      }

      // 2. ì´ë¯¸ì§€ ì²˜ë¦¬
      const { optimizedBuffer, imageHash } = await this.processImage(base64Image);

      // 3. ìºì‹± ì²´í¬
      const cacheKey = `art:${userId}:${styleId}:${imageHash}`;
      const cached = await redis.get(cacheKey);
      if (cached) {
        logger.info('Returning cached art profile');
        return JSON.parse(cached);
      }

      // 4. ì›ë³¸ ì´ë¯¸ì§€ Cloudinary ì—…ë¡œë“œ
      const originalImageUrl = await this.uploadToCloudinary(optimizedBuffer, 'originals');

      // 5. ìŠ¤íƒ€ì¼ ì„¤ì • ê°€ì ¸ì˜¤ê¸°
      const styleConfig = this.styleModels[styleId];
      if (!styleConfig) {
        throw new Error('Invalid style ID');
      }

      // 6. Replicate API í˜¸ì¶œ (ì¬ì‹œë„ ë¡œì§ í¬í•¨)
      logger.info(`Generating art profile with style: ${styleId}`);

      const output = await this.generateWithRetry(styleConfig, originalImageUrl, styleId, customSettings);

      // 7. ê²°ê³¼ ì´ë¯¸ì§€ Cloudinaryì— ì €ì¥
      const transformedImageUrl = output[0];

      // 8. ë°ì´í„°ë² ì´ìŠ¤ì— ì €ì¥
      const artProfileId = uuidv4();
      const insertQuery = `
        INSERT INTO art_profiles (
          id, user_id, original_image, transformed_image, 
          style_id, settings, created_at
        ) VALUES ($1, $2, $3, $4, $5, $6, NOW())
        RETURNING *
      `;

      const values = [
        artProfileId,
        userId,
        originalImageUrl,
        transformedImageUrl,
        styleId,
        JSON.stringify(customSettings)
      ];

      const result = await hybridDB.query(insertQuery, values);
      const artProfile = result.rows[0];

      // 9. ê²°ê³¼ ìºì‹± (1ì‹œê°„)
      const cacheData = {
        id: artProfile.id,
        userId: artProfile.user_id,
        originalImage: artProfile.original_image,
        transformedImage: artProfile.transformed_image,
        styleUsed: {
          id: styleId,
          ...styleConfig
        },
        createdAt: artProfile.created_at
      };

      await redis.setex(cacheKey, 3600, JSON.stringify(cacheData));

      // 10. ì‚¬ìš©ëŸ‰ ë¡œê¹…
      logger.info(`Art profile generated: ${artProfileId} for user: ${userId}`);

      return cacheData;
    } catch (error) {
      // ìƒì„¸í•œ ì—ëŸ¬ ë¡œê¹… ë° ë¶„ë¥˜
      const errorInfo = {
        userId,
        styleId,
        error: error.message,
        stack: error.stack,
        timestamp: new Date().toISOString()
      };

      logger.error('Error generating art profile:', errorInfo);

      // ì—ëŸ¬ íƒ€ì…ë³„ ì²˜ë¦¬
      if (error.message.includes('No credits remaining')) {
        throw new Error('INSUFFICIENT_CREDITS');
      } else if (error.message.includes('NSFW') || error.message.includes('content detected')) {
        throw new Error('CONTENT_POLICY_VIOLATION');
      } else if (error.message.includes('Invalid style ID')) {
        throw new Error('INVALID_STYLE');
      } else if (error.message.includes('Failed to generate art profile after')) {
        throw new Error('GENERATION_FAILED');
      } else {
        throw new Error('UNKNOWN_ERROR');
      }
    }
  }

  /**
   * ì„±ëŠ¥ í†µê³„ ì¡°íšŒ (ê´€ë¦¬ììš©)
   */
  async getPerformanceStats(date = new Date().toISOString().split('T')[0]) {
    try {
      const statsKey = `stats:art-profile:${date}`;
      const stats = await redis.hgetall(statsKey);

      const result = {};
      Object.keys(this.styleModels).forEach(styleId => {
        result[styleId] = {
          attempts: parseInt(stats[`${styleId}:attempts`] || 0),
          success: parseInt(stats[`${styleId}:success`] || 0),
          failure: parseInt(stats[`${styleId}:failure`] || 0),
          successRate: stats[`${styleId}:success`] && stats[`${styleId}:failure`]
            ? (parseInt(stats[`${styleId}:success`]) / (parseInt(stats[`${styleId}:success`]) + parseInt(stats[`${styleId}:failure`])) * 100).toFixed(2)
            : 0
        };
      });

      return result;
    } catch (error) {
      logger.error('Failed to get performance stats:', error);
      return {};
    }
  }

  /**
   * ì‚¬ìš©ìì˜ ì „ì‹œ ê´€ëŒ ê¸°ë¡ì„ ë°”íƒ•ìœ¼ë¡œ ì¶”ì²œ ìŠ¤íƒ€ì¼ ê°€ì ¸ì˜¤ê¸°
   */
  async getRecommendedStyles(userId) {
    try {
      // ì‚¬ìš©ìì˜ ìµœê·¼ ì „ì‹œ ê´€ëŒ ê¸°ë¡ì—ì„œ ì„ í˜¸ ìŠ¤íƒ€ì¼ ë¶„ì„
      const query = `
        SELECT 
          e.tags,
          COUNT(*) as visit_count
        FROM exhibition_visits ev
        JOIN exhibitions e ON ev.exhibition_id = e.id
        WHERE ev.user_id = $1
          AND ev.visited_at >= CURRENT_DATE - INTERVAL '3 months'
        GROUP BY e.tags
        ORDER BY visit_count DESC
        LIMIT 10
      `;

      const result = await hybridDB.query(query, [userId]);

      // íƒœê·¸ ë¶„ì„í•˜ì—¬ ì¶”ì²œ ìŠ¤íƒ€ì¼ ê²°ì •
      const recommendedStyles = [];
      const allStyles = Object.keys(this.styleModels);

      // íƒœê·¸ ê¸°ë°˜ ë§¤ì¹­ ë¡œì§
      result.rows.forEach(row => {
        if (row.tags) {
          const tags = row.tags.toLowerCase();

          if (tags.includes('ì¸ìƒ') || tags.includes('impression')) {
            recommendedStyles.push('monet-impressionism');
          }
          if (tags.includes('í˜„ëŒ€') || tags.includes('contemporary')) {
            recommendedStyles.push('warhol-popart', 'mondrian-neoplasticism');
          }
          if (tags.includes('í•œêµ­') || tags.includes('korean')) {
            recommendedStyles.push('korean-minhwa');
          }
          if (tags.includes('ë””ì§€í„¸') || tags.includes('digital')) {
            recommendedStyles.push('pixel-art');
          }
        }
      });

      // ì¤‘ë³µ ì œê±° ë° ìƒìœ„ 3ê°œ ë°˜í™˜
      const uniqueStyles = [...new Set(recommendedStyles)].slice(0, 3);

      // ì¶”ì²œì´ ë¶€ì¡±í•˜ë©´ ì¸ê¸° ìŠ¤íƒ€ì¼ ì¶”ê°€
      if (uniqueStyles.length < 3) {
        const popularStyles = ['monet-impressionism', 'vangogh-postimpressionism', 'pixel-art'];
        popularStyles.forEach(style => {
          if (!uniqueStyles.includes(style) && uniqueStyles.length < 3) {
            uniqueStyles.push(style);
          }
        });
      }

      return uniqueStyles.map(styleId => ({
        id: styleId,
        ...this.styleModels[styleId]
      }));
    } catch (error) {
      logger.error('Error getting recommended styles:', error);
      // ì—ëŸ¬ ì‹œ ê¸°ë³¸ ì¶”ì²œ ë°˜í™˜
      return ['monet-impressionism', 'pixel-art', 'warhol-popart'].map(id => ({
        id,
        ...this.styleModels[id]
      }));
    }
  }

  /**
   * ê°¤ëŸ¬ë¦¬ìš© ì•„íŠ¸ í”„ë¡œí•„ ëª©ë¡ ê°€ì ¸ì˜¤ê¸°
   */
  async getGallery(filter = {}) {
    try {
      let query = `
        SELECT 
          ap.*,
          u.nickname,
          u.personality_type,
          COUNT(apl.id) as like_count
        FROM art_profiles ap
        JOIN users u ON ap.user_id = u.id
        LEFT JOIN art_profile_likes apl ON ap.id = apl.art_profile_id
        WHERE ap.is_public = true
      `;

      const values = [];
      let valueIndex = 1;

      // ìŠ¤íƒ€ì¼ í•„í„°
      if (filter.style) {
        query += ` AND ap.style_id = $${valueIndex}`;
        values.push(filter.style);
        valueIndex++;
      }

      // ê¸°ê°„ í•„í„°
      if (filter.period) {
        const periodMap = {
          'today': '1 day',
          'week': '7 days',
          'month': '30 days'
        };
        query += ` AND ap.created_at >= CURRENT_DATE - INTERVAL '${periodMap[filter.period]}'`;
      }

      query += ` GROUP BY ap.id, u.id, u.nickname, u.personality_type`;

      // ì •ë ¬
      if (filter.sort === 'popular') {
        query += ` ORDER BY like_count DESC, ap.created_at DESC`;
      } else {
        query += ` ORDER BY ap.created_at DESC`;
      }

      query += ` LIMIT 50`;

      const result = await hybridDB.query(query, values);

      return result.rows.map(row => ({
        id: row.id,
        user: {
          id: row.user_id,
          nickname: row.nickname,
          personalityType: row.personality_type
        },
        artProfile: {
          transformedImage: row.transformed_image,
          styleUsed: this.styleModels[row.style_id] || {},
          createdAt: row.created_at
        },
        likeCount: parseInt(row.like_count || 0)
      }));
    } catch (error) {
      logger.error('Error fetching gallery:', error);
      throw error;
    }
  }

  /**
   * ì•„íŠ¸ í”„ë¡œí•„ ì¢‹ì•„ìš”
   */
  async likeArtProfile(artProfileId, userId) {
    try {
      // ì´ë¯¸ ì¢‹ì•„ìš” í–ˆëŠ”ì§€ í™•ì¸
      const checkQuery = `
        SELECT id FROM art_profile_likes 
        WHERE art_profile_id = $1 AND user_id = $2
      `;
      const existing = await hybridDB.query(checkQuery, [artProfileId, userId]);

      if (existing.rows.length > 0) {
        // ì´ë¯¸ ì¢‹ì•„ìš”í•œ ê²½ìš° ì·¨ì†Œ
        await hybridDB.query(
          'DELETE FROM art_profile_likes WHERE art_profile_id = $1 AND user_id = $2',
          [artProfileId, userId]
        );
        return { liked: false };
      } else {
        // ì¢‹ì•„ìš” ì¶”ê°€
        await hybridDB.query(
          'INSERT INTO art_profile_likes (art_profile_id, user_id) VALUES ($1, $2)',
          [artProfileId, userId]
        );
        return { liked: true };
      }
    } catch (error) {
      logger.error('Error liking art profile:', error);
      throw error;
    }
  }
}

module.exports = new ArtProfileService();

const { getSupabaseAdmin } = require('../config/supabase');
const redisClient = require('../config/redis');
const { logger } = require('../config/logger');
const cron = require('node-cron');

class ArtPulseService {
  constructor() {
    this.currentSession = null;
    this.sessionParticipants = new Map(); // sessionId -> Set of userIds
    this.emotionDistribution = new Map(); // sessionId -> emotion counts
    this.realTimeReflections = new Map(); // sessionId -> reflection array
    this.votingResults = new Map(); // sessionId -> voting data

    // Schedule daily Art Pulse sessions at 7 PM KST
    this.scheduleDailySessions();
  }

  // Daily session scheduler
  scheduleDailySessions() {
    // Run at 7 PM KST every day
    cron.schedule('0 19 * * *', async () => {
      try {
        await this.startDailyArtPulse();
        logger.info('Daily Art Pulse session started automatically');
      } catch (error) {
        logger.error('Failed to start daily Art Pulse session:', error);
      }
    }, {
      timezone: 'Asia/Seoul'
    });

    logger.info('Art Pulse daily scheduler initialized (7 PM KST)');
  }

  // Start a new Art Pulse session
  async startDailyArtPulse() {
    try {
      const artwork = await this.selectTodaysArtwork();
      const sessionId = `art-pulse-${Date.now()}`;

      const session = {
        id: sessionId,
        artwork,
        startTime: new Date(),
        endTime: new Date(Date.now() + 20 * 60 * 1000), // 20 minutes
        status: 'active',
        phase: 'contemplation', // contemplation -> sharing -> voting
        participantCount: 0,
        emotionDistribution: {},
        reflections: []
      };

      // Save to database
      const supabase = getSupabaseAdmin();
      await supabase.from('art_pulse_sessions').insert({
        id: sessionId,
        artwork_id: artwork.id,
        artwork_data: artwork,
        start_time: session.startTime,
        end_time: session.endTime,
        status: 'active',
        phase: 'contemplation'
      });

      // Cache in Redis
      await redisClient.setex(`art-pulse:session:${sessionId}`, 1800, JSON.stringify(session));
      await redisClient.set('art-pulse:current-session', sessionId);

      this.currentSession = session;
      this.sessionParticipants.set(sessionId, new Set());
      this.emotionDistribution.set(sessionId, {});
      this.realTimeReflections.set(sessionId, []);

      // Schedule phase transitions
      this.schedulePhaseTransitions(sessionId);

      return session;
    } catch (error) {
      logger.error('Error starting Art Pulse session:', error);
      throw error;
    }
  }

  // Schedule phase transitions for a session
  schedulePhaseTransitions(sessionId) {
    // Transition to sharing phase after 8 minutes
    setTimeout(async () => {
      await this.transitionPhase(sessionId, 'sharing');
    }, 8 * 60 * 1000);

    // Transition to voting phase after 15 minutes
    setTimeout(async () => {
      await this.transitionPhase(sessionId, 'voting');
    }, 15 * 60 * 1000);

    // End session after 20 minutes
    setTimeout(async () => {
      await this.endSession(sessionId);
    }, 20 * 60 * 1000);
  }

  // Select today's artwork using intelligent algorithm
  async selectTodaysArtwork() {
    try {
      const supabase = getSupabaseAdmin();

      // Get artwork that hasn't been featured recently
      const { data: recentSessions } = await supabase
        .from('art_pulse_sessions')
        .select('artwork_id')
        .gte('start_time', new Date(Date.now() - 30 * 24 * 60 * 60 * 1000))
        .order('start_time', { ascending: false });

      const recentArtworkIds = recentSessions?.map(s => s.artwork_id) || [];

      // Selection criteria: emotional depth, visual impact, discussion potential
      const { data: candidates } = await supabase
        .from('artworks')
        .select(`
          *,
          artwork_analytics (
            emotional_depth_score,
            visual_impact_score,
            discussion_potential_score
          )
        `)
        .not('id', 'in', `(${recentArtworkIds.join(',') || '0'})`)
        .gte('artwork_analytics.emotional_depth_score', 0.7)
        .order('artwork_analytics.discussion_potential_score', { ascending: false })
        .limit(10);

      if (!candidates?.length) {
        // Fallback to any available artwork
        const { data: fallback } = await supabase
          .from('artworks')
          .select('*')
          .limit(1);
        return fallback[0];
      }

      // Weight by time of day and seasonal factors
      const now = new Date();
      const hour = now.getHours();
      const season = this.getCurrentSeason();

      const weighted = candidates.map(artwork => {
        let score = artwork.artwork_analytics?.discussion_potential_score || 0.5;

        // Evening preference for contemplative pieces
        if (hour >= 18 && artwork.mood_tags?.includes('contemplative')) {
          score += 0.2;
        }

        // Seasonal adjustments
        if (artwork.season_tags?.includes(season)) {
          score += 0.1;
        }

        return { ...artwork, weighted_score: score };
      });

      // Select with weighted randomness
      weighted.sort((a, b) => b.weighted_score - a.weighted_score);
      const topCandidates = weighted.slice(0, 3);
      const selected = topCandidates[Math.floor(Math.random() * topCandidates.length)];

      logger.info(`Selected artwork for Art Pulse: ${selected.title} by ${selected.artist}`);
      return selected;
    } catch (error) {
      logger.error('Error selecting artwork:', error);
      throw error;
    }
  }

  // Get current season for artwork selection
  getCurrentSeason() {
    const month = new Date().getMonth() + 1;
    if (month >= 3 && month <= 5) return 'spring';
    if (month >= 6 && month <= 8) return 'summer';
    if (month >= 9 && month <= 11) return 'autumn';
    return 'winter';
  }

  // User joins Art Pulse session
  async joinSession(userId, sessionId = null) {
    try {
      const targetSessionId = sessionId || await redisClient.get('art-pulse:current-session');
      if (!targetSessionId) {
        throw new Error('No active Art Pulse session');
      }

      // Get user profile for personalization
      const supabase = getSupabaseAdmin();
      const { data: profile } = await supabase
        .from('profiles')
        .select('id, username, sayu_type, emotion_preferences, art_preferences')
        .eq('id', userId)
        .single();

      if (!profile) {
        throw new Error('User profile not found');
      }

      // Add to participants
      if (!this.sessionParticipants.has(targetSessionId)) {
        this.sessionParticipants.set(targetSessionId, new Set());
      }
      this.sessionParticipants.get(targetSessionId).add(userId);

      // Update Redis
      await redisClient.sadd(`art-pulse:participants:${targetSessionId}`, userId);

      // Log participation
      await supabase.from('art_pulse_participations').insert({
        session_id: targetSessionId,
        user_id: userId,
        joined_at: new Date(),
        sayu_type: profile.sayu_type
      });

      const session = await this.getSession(targetSessionId);
      return {
        session,
        userProfile: profile,
        participantCount: this.sessionParticipants.get(targetSessionId).size
      };
    } catch (error) {
      logger.error('Error joining Art Pulse session:', error);
      throw error;
    }
  }

  // Submit emotion during contemplation phase
  async submitEmotion(userId, sessionId, emotion, intensity = 1) {
    try {
      const session = await this.getSession(sessionId);
      if (session.phase !== 'contemplation') {
        throw new Error('Emotion submission only available during contemplation phase');
      }

      // Validate emotion
      const validEmotions = [
        'wonder', 'melancholy', 'joy', 'contemplation', 'nostalgia',
        'awe', 'serenity', 'passion', 'mystery', 'hope'
      ];

      if (!validEmotions.includes(emotion)) {
        throw new Error('Invalid emotion');
      }

      // Update emotion distribution
      if (!this.emotionDistribution.has(sessionId)) {
        this.emotionDistribution.set(sessionId, {});
      }

      const distribution = this.emotionDistribution.get(sessionId);
      if (!distribution[emotion]) {
        distribution[emotion] = [];
      }

      // Remove previous emotion from this user
      Object.keys(distribution).forEach(key => {
        distribution[key] = distribution[key].filter(entry => entry.userId !== userId);
      });

      // Add new emotion
      distribution[emotion].push({
        userId,
        intensity,
        timestamp: new Date()
      });

      // Update Redis
      await redisClient.hset(
        `art-pulse:emotions:${sessionId}`,
        emotion,
        JSON.stringify(distribution[emotion])
      );

      // Save to database
      const supabase = getSupabaseAdmin();
      await supabase.from('art_pulse_emotions').upsert({
        session_id: sessionId,
        user_id: userId,
        emotion,
        intensity,
        submitted_at: new Date()
      });

      return this.getEmotionDistribution(sessionId);
    } catch (error) {
      logger.error('Error submitting emotion:', error);
      throw error;
    }
  }

  // Submit reflection during sharing phase
  async submitReflection(userId, sessionId, reflection, isAnonymous = false) {
    try {
      const session = await this.getSession(sessionId);
      if (session.phase !== 'sharing') {
        throw new Error('Reflection submission only available during sharing phase');
      }

      const supabase = getSupabaseAdmin();

      // Get user profile
      const { data: profile } = await supabase
        .from('profiles')
        .select('username, sayu_type, profile_image')
        .eq('id', userId)
        .single();

      const reflectionData = {
        id: `reflection-${Date.now()}-${userId}`,
        userId: isAnonymous ? null : userId,
        username: isAnonymous ? null : profile.username,
        sayuType: isAnonymous ? null : profile.sayu_type,
        profileImage: isAnonymous ? null : profile.profile_image,
        reflection: reflection.trim(),
        timestamp: new Date(),
        likes: 0,
        likedBy: [],
        isAnonymous
      };

      // Add to real-time reflections
      if (!this.realTimeReflections.has(sessionId)) {
        this.realTimeReflections.set(sessionId, []);
      }
      this.realTimeReflections.get(sessionId).push(reflectionData);

      // Save to database
      await supabase.from('art_pulse_reflections').insert({
        id: reflectionData.id,
        session_id: sessionId,
        user_id: userId,
        reflection,
        is_anonymous: isAnonymous,
        created_at: new Date()
      });

      // Update Redis
      await redisClient.lpush(
        `art-pulse:reflections:${sessionId}`,
        JSON.stringify(reflectionData)
      );

      return reflectionData;
    } catch (error) {
      logger.error('Error submitting reflection:', error);
      throw error;
    }
  }

  // Like a reflection
  async likeReflection(userId, sessionId, reflectionId) {
    try {
      const reflections = this.realTimeReflections.get(sessionId) || [];
      const reflection = reflections.find(r => r.id === reflectionId);

      if (!reflection) {
        throw new Error('Reflection not found');
      }

      // Toggle like
      const likedIndex = reflection.likedBy.indexOf(userId);
      if (likedIndex === -1) {
        reflection.likedBy.push(userId);
        reflection.likes++;
      } else {
        reflection.likedBy.splice(likedIndex, 1);
        reflection.likes--;
      }

      // Update database
      const supabase = getSupabaseAdmin();
      await supabase.from('art_pulse_reflection_likes').upsert({
        reflection_id: reflectionId,
        user_id: userId,
        liked_at: likedIndex === -1 ? new Date() : null
      });

      return reflection;
    } catch (error) {
      logger.error('Error liking reflection:', error);
      throw error;
    }
  }

  // Transition session phase
  async transitionPhase(sessionId, newPhase) {
    try {
      const session = await this.getSession(sessionId);
      if (!session) return;

      session.phase = newPhase;

      // Update database
      const supabase = getSupabaseAdmin();
      await supabase.from('art_pulse_sessions')
        .update({ phase: newPhase })
        .eq('id', sessionId);

      // Update Redis
      await redisClient.setex(`art-pulse:session:${sessionId}`, 1800, JSON.stringify(session));

      // Broadcast phase change to all participants
      const socketService = require('./socketService');
      socketService.broadcastArtPulsePhaseChange(sessionId, newPhase);

      logger.info(`Art Pulse session ${sessionId} transitioned to ${newPhase} phase`);
      return session;
    } catch (error) {
      logger.error('Error transitioning phase:', error);
      throw error;
    }
  }

  // End session and calculate results
  async endSession(sessionId) {
    try {
      const session = await this.getSession(sessionId);
      if (!session) return;

      // Calculate session results
      const results = await this.calculateSessionResults(sessionId);

      session.status = 'completed';
      session.results = results;

      // Update database
      const supabase = getSupabaseAdmin();
      await supabase.from('art_pulse_sessions')
        .update({
          status: 'completed',
          results,
          ended_at: new Date()
        })
        .eq('id', sessionId);

      // Broadcast session end to all participants
      const socketService = require('./socketService');
      socketService.broadcastArtPulseSessionEnd(sessionId, results);

      // Award achievements
      await this.awardSessionAchievements(sessionId, results);

      // Clean up memory
      this.sessionParticipants.delete(sessionId);
      this.emotionDistribution.delete(sessionId);
      this.realTimeReflections.delete(sessionId);
      this.votingResults.delete(sessionId);

      // Clear current session if this was it
      const currentSessionId = await redisClient.get('art-pulse:current-session');
      if (currentSessionId === sessionId) {
        await redisClient.del('art-pulse:current-session');
        this.currentSession = null;
      }

      logger.info(`Art Pulse session ${sessionId} ended with ${results.totalParticipants} participants`);
      return results;
    } catch (error) {
      logger.error('Error ending session:', error);
      throw error;
    }
  }

  // Calculate session results
  async calculateSessionResults(sessionId) {
    try {
      const participants = this.sessionParticipants.get(sessionId) || new Set();
      const emotions = this.emotionDistribution.get(sessionId) || {};
      const reflections = this.realTimeReflections.get(sessionId) || [];

      // Top emotions
      const emotionCounts = {};
      Object.keys(emotions).forEach(emotion => {
        emotionCounts[emotion] = emotions[emotion].length;
      });

      const topEmotions = Object.entries(emotionCounts)
        .sort(([,a], [,b]) => b - a)
        .slice(0, 3)
        .map(([emotion, count]) => ({ emotion, count }));

      // Top reflections (by likes)
      const topReflections = reflections
        .filter(r => !r.isAnonymous) // Only public reflections for awards
        .sort((a, b) => b.likes - a.likes)
        .slice(0, 3);

      // Participation stats
      const supabase = getSupabaseAdmin();
      const { data: sayuTypeStats } = await supabase
        .from('art_pulse_participations')
        .select('sayu_type')
        .eq('session_id', sessionId);

      const sayuDistribution = {};
      sayuTypeStats?.forEach(({ sayu_type }) => {
        sayuDistribution[sayu_type] = (sayuDistribution[sayu_type] || 0) + 1;
      });

      return {
        totalParticipants: participants.size,
        topEmotions,
        topReflections,
        sayuDistribution,
        totalReflections: reflections.length,
        emotionDiversity: Object.keys(emotions).length,
        averageEngagement: reflections.reduce((sum, r) => sum + r.likes, 0) / Math.max(reflections.length, 1)
      };
    } catch (error) {
      logger.error('Error calculating session results:', error);
      throw error;
    }
  }

  // Award achievements for session participation
  async awardSessionAchievements(sessionId, results) {
    try {
      const supabase = getSupabaseAdmin();

      // Award "Deep Reflection" achievement to top reflection authors
      for (const reflection of results.topReflections) {
        if (reflection.userId) {
          await supabase.from('user_achievements').upsert({
            user_id: reflection.userId,
            achievement_id: 'art-pulse-deep-reflection',
            earned_at: new Date(),
            session_id: sessionId
          });
        }
      }

      // Award "Emotion Pioneer" to users who selected unique emotions
      const participants = this.sessionParticipants.get(sessionId) || new Set();
      for (const userId of participants) {
        // Check if user contributed to emotional diversity
        const userEmotions = await supabase
          .from('art_pulse_emotions')
          .select('emotion')
          .eq('session_id', sessionId)
          .eq('user_id', userId);

        if (userEmotions.data?.length > 0) {
          await supabase.from('user_achievements').upsert({
            user_id: userId,
            achievement_id: 'art-pulse-emotion-pioneer',
            earned_at: new Date(),
            session_id: sessionId
          });
        }
      }
    } catch (error) {
      logger.error('Error awarding achievements:', error);
    }
  }

  // Get current session
  async getCurrentSession() {
    try {
      const sessionId = await redisClient.get('art-pulse:current-session');
      if (!sessionId) return null;

      return await this.getSession(sessionId);
    } catch (error) {
      logger.error('Error getting current session:', error);
      return null;
    }
  }

  // Get session by ID
  async getSession(sessionId) {
    try {
      // Try Redis first
      const cached = await redisClient.get(`art-pulse:session:${sessionId}`);
      if (cached) {
        return JSON.parse(cached);
      }

      // Fallback to database
      const supabase = getSupabaseAdmin();
      const { data: session } = await supabase
        .from('art_pulse_sessions')
        .select('*')
        .eq('id', sessionId)
        .single();

      return session;
    } catch (error) {
      logger.error('Error getting session:', error);
      return null;
    }
  }

  // Get real-time emotion distribution
  getEmotionDistribution(sessionId) {
    const emotions = this.emotionDistribution.get(sessionId) || {};
    const distribution = {};

    Object.keys(emotions).forEach(emotion => {
      distribution[emotion] = {
        count: emotions[emotion].length,
        intensity: emotions[emotion].reduce((sum, e) => sum + e.intensity, 0) / emotions[emotion].length || 0
      };
    });

    return distribution;
  }

  // Get real-time reflections
  getReflections(sessionId) {
    return this.realTimeReflections.get(sessionId) || [];
  }

  // Get participant count
  getParticipantCount(sessionId) {
    return this.sessionParticipants.get(sessionId)?.size || 0;
  }

  // Weekly reflection league
  async getWeeklyLeague() {
    try {
      const supabase = getSupabaseAdmin();
      const weekStart = new Date();
      weekStart.setDate(weekStart.getDate() - weekStart.getDay()); // Start of current week

      const { data: topReflections } = await supabase
        .from('art_pulse_reflections')
        .select(`
          *,
          profiles!user_id (username, sayu_type, profile_image),
          art_pulse_reflection_likes (count)
        `)
        .gte('created_at', weekStart.toISOString())
        .eq('is_anonymous', false)
        .order('art_pulse_reflection_likes.count', { ascending: false })
        .limit(10);

      return topReflections || [];
    } catch (error) {
      logger.error('Error getting weekly league:', error);
      return [];
    }
  }
}

module.exports = new ArtPulseService();

