   */
  deduplicateWorks(works) {
    const seen = new Set();
    return works.filter(work => {
      const key = `${work.title.toLowerCase()}_${work.year}`;
      if (seen.has(key)) {
        return false;
      }
      seen.add(key);
      return true;
    });
  }

