  /**
   * ì´ë¯¸ì§€ì—ì„œ ì£¼ìš” ìƒ‰ìƒ ì¶”ì¶œ
   */
  async analyzeImageColors(imageUrl) {
    try {
      if (!imageUrl) return [];

      // ì´ë¯¸ì§€ ë‹¤ìš´ë¡œë“œ
      const response = await axios.get(imageUrl, { responseType: 'arraybuffer' });
      const buffer = Buffer.from(response.data);

      // Sharpë¡œ ì´ë¯¸ì§€ ì²˜ë¦¬ ë° ìƒ‰ìƒ ì¶”ì¶œ
      const { dominant } = await sharp(buffer).stats();

      // RGBë¥¼ HEXë¡œ ë³€í™˜
      const rgbToHex = (r, g, b) => `#${[r, g, b].map(x => {
        const hex = x.toString(16);
        return hex.length === 1 ? `0${hex}` : hex;
      }).join('')}`;

      return [rgbToHex(dominant.r, dominant.g, dominant.b)];
    } catch (error) {
      console.error('Error analyzing image colors:', error);
      return [];
    }
  }

  /**
   * ì£¼ìš” ìƒ‰ìƒ ì°¾ê¸°
   */
  findDominantColors(colors) {
    // ìƒ‰ìƒ ë¹ˆë„ ê³„ì‚°
    const colorFrequency = {};
    colors.forEach(color => {
      if (color) {
        colorFrequency[color] = (colorFrequency[color] || 0) + 1;
      }
    });

    // ë¹ˆë„ìˆœìœ¼ë¡œ ì •ë ¬í•˜ì—¬ ìƒìœ„ 5ê°œ ë°˜í™˜
    return Object.entries(colorFrequency)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 5)
      .map(([color]) => color);
  }

