  // ì§„í–‰ ìƒí™© ë¡œë“œ
  async loadProgress() {
    try {
      const data = await fs.readFile(this.progressFile, 'utf8');
      return JSON.parse(data);
    } catch (error) {
      return {
        completedCities: [],
        totalStats: {
          exhibitions: 0,
          venues: 0,
          errors: 0
        },
