  /**
   * í•œêµ­ ë¯¸ìˆ ê´€/ê°¤ëŸ¬ë¦¬ ì§ì ‘ ê²€ìƒ‰
   */
  async searchKoreanVenues() {
    console.log('\n=== Searching for Korean venues ===\n');

    const venues = [];
    const searchTerms = ['seoul', 'korea', 'mmca', 'leeum', 'sema'];

    for (const term of searchTerms) {
