  async convertToVector(interpretation) {
    // ë‹¤ì°¨ì› ê°ì • ê³µê°„ì—ì„œì˜ ìœ„ì¹˜ë¥¼ ë²¡í„°ë¡œ í‘œí˜„
    const vector = [
