        await client.query('COMMIT');

        logger.info(`âœ… ê¸°ì¡´ ì•„í‹°ìŠ¤íŠ¸ ì •ë³´ ì—…ë°ì´íŠ¸: ${artistData.name}`);
        return result.rows[0];

      } else {
        // ìƒˆ ì•„í‹°ìŠ¤íŠ¸ ì‚½ì…
        const insertQuery = `
          INSERT INTO artists (
            name, name_ko, birth_year, death_year, nationality, nationality_ko,
