        ]);

        await client.query('COMMIT');

        logger.info(`âœ… ìƒˆ ì•„í‹°ìŠ¤íŠ¸ ì •ë³´ ì €ì¥: ${artistData.name}`);
        return result.rows[0];
      }

    } catch (error) {
      await client.query('ROLLBACK');
      logger.error('ì•„í‹°ìŠ¤íŠ¸ DB ì €ì¥ ì‹¤íŒ¨', error);
