  async sendMatchConfirmationNotifications(matchRequest, matchedUserId) {
    // ì–‘ìª½ ì‚¬ìš©ìì—ê²Œ í™•ì • ì•Œë¦¼
    const notifications = [
      {
        userId: matchRequest.host_user_id,
