    const geoKey = 'matching:pool:geo';
    const metaKey = `matching:pool:meta:${matchRequest.id}`;

    // ì§€ë¦¬ì  ìœ„ì¹˜ë¡œ ì¸ë±ì‹±
    await redis.geoadd(
