  parseDetailedScores(text) {
    const scores = {
      L_S: { main: 0, sub: { individual: 0, introspective: 0, sociability: 0, collaborative: 0 } },
      A_R: { main: 0, sub: { atmospheric: 0, symbolic: 0, realistic: 0, concrete: 0 } },
      E_M: { main: 0, sub: { emotional: 0, affective: 0, analytical: 0, rational: 0 } },
      F_C: { main: 0, sub: { fluid: 0, spontaneous: 0, structured: 0, systematic: 0 } },
      analysis: ''
    };

    // ì£¼ ì ìˆ˜ ì¶”ì¶œ
    const mainPatterns = {
      L_S: /L\/S ì ìˆ˜:\s*([+-]?\d+)/i,
      A_R: /A\/R ì ìˆ˜:\s*([+-]?\d+)/i,
      E_M: /E\/M ì ìˆ˜:\s*([+-]?\d+)/i,
      F_C: /F\/C ì ìˆ˜:\s*([+-]?\d+)/i
    };

    for (const [axis, pattern] of Object.entries(mainPatterns)) {
      const match = text.match(pattern);
      if (match) {
        scores[axis].main = parseInt(match[1]);
      }
    }

    // ì„¸ë¶€ ì ìˆ˜ ì¶”ì¶œ
    const subPatterns = {
      'ê°œì¸ì„±': (v) => scores.L_S.sub.individual = parseInt(v),
      'ë‚´ì„±ì ': (v) => scores.L_S.sub.introspective = parseInt(v),
      'ì‚¬êµì„±': (v) => scores.L_S.sub.sociability = parseInt(v),
      'í˜‘ë ¥ì„±': (v) => scores.L_S.sub.collaborative = parseInt(v),
      'ë¶„ìœ„ê¸°ì„±': (v) => scores.A_R.sub.atmospheric = parseInt(v),
      'ìƒì§•ì„±': (v) => scores.A_R.sub.symbolic = parseInt(v),
      'í˜„ì‹¤ì„±': (v) => scores.A_R.sub.realistic = parseInt(v),
      'êµ¬ì²´ì„±': (v) => scores.A_R.sub.concrete = parseInt(v),
      'ì •ì„œì„±': (v) => scores.E_M.sub.emotional = parseInt(v),
      'ê°ì •ì„±': (v) => scores.E_M.sub.affective = parseInt(v),
      'ë¶„ì„ì„±': (v) => scores.E_M.sub.analytical = parseInt(v),
      'ì´ì„±ì„±': (v) => scores.E_M.sub.rational = parseInt(v),
      'ìœ ë™ì„±': (v) => scores.F_C.sub.fluid = parseInt(v),
      'ìë°œì„±': (v) => scores.F_C.sub.spontaneous = parseInt(v),
      'êµ¬ì¡°ì„±': (v) => scores.F_C.sub.structured = parseInt(v),
      'ì²´ê³„ì„±': (v) => scores.F_C.sub.systematic = parseInt(v)
    };

    for (const [name, setter] of Object.entries(subPatterns)) {
      const pattern = new RegExp(`${name}:\\s*([+-]?\\d+)`, 'i');
      const match = text.match(pattern);
      if (match) {
        setter(match[1]);
      }
    }

    // ì¢…í•© ë¶„ì„ ì¶”ì¶œ
    const analysisMatch = text.match(/ì¢…í•© ë¶„ì„:\s*(.+?)$/ims);
    if (analysisMatch) {
      scores.analysis = analysisMatch[1].trim();
    }

    return scores;
  }

  inferDetailedScores(data) {
