  }

  // ë ˆë²¨ì—… ë³´ìƒ
  async grantLevelRewards(userId, newLevel) {
    const rewards = {
      2: { type: 'custom_profile', name: 'ì»¤ìŠ¤í…€ í”„ë¡œí•„ ì ê¸ˆ í•´ì œ' },
      3: { type: 'priority_matching', name: 'ìš°ì„  ë§¤ì¹­ ê¸°ëŠ¥' },
