        return result.rows[0];
      }

    } catch (error) {
      await client.query('ROLLBACK');
      logger.error('ë³‘í•©ëœ ì•„í‹°ìŠ¤íŠ¸ DB ì €ì¥ ì‹¤íŒ¨', error);
      throw error;
    } finally {
      client.release();
    }
  }

  /**
