  extractCity(address) {
    if (!address) return 'ì„œìš¸';

    const cityPatterns = [
      /ì„œìš¸íŠ¹ë³„ì‹œ|ì„œìš¸ì‹œ|ì„œìš¸/,
      /ë¶€ì‚°ê´‘ì—­ì‹œ|ë¶€ì‚°ì‹œ|ë¶€ì‚°/,
