    // ì œëª©ì—ì„œ íƒœê·¸ ì¶”ì¶œ
    const title = item.title || '';
    if (title.includes('í˜„ëŒ€ë¯¸ìˆ ')) tags.push('í˜„ëŒ€ë¯¸ìˆ ');
    if (title.includes('ê¸°íšì „')) tags.push('ê¸°íšì „');
    if (title.includes('ê°œì¸ì „')) tags.push('ê°œì¸ì „');
    if (title.includes('ë‹¨ì²´ì „')) tags.push('ë‹¨ì²´ì „');
    if (title.includes('ì‚¬ì§„')) tags.push('ì‚¬ì§„');
    if (title.includes('ì¡°ê°')) tags.push('ì¡°ê°');
    if (title.includes('íšŒí™”')) tags.push('íšŒí™”');

    return tags.slice(0, 5); // ìµœëŒ€ 5ê°œ
  }

  // ì›”ë³„ ì „ì‹œ í†µê³„ ì—…ë°ì´íŠ¸
  async updateMonthlyStats() {
    try {
      const { data: stats } = await this.supabase
        .from('exhibitions')
        .select('status, created_at')
        .gte('created_at', new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString());

      const summary = {
