/**
 * Vector Similarity Service
 * Implements true semantic search using pgvector
 * Provides user similarity, artwork recommendations, and semantic search
 */

const { pool } = require('../config/database');
const { log } = require('../config/logger');
const { getRedisClient } = require('../config/redis');
const openaiService = require('./openai');

class VectorSimilarityService {
  constructor() {
    this.redis = getRedisClient();
    this.cachePrefix = 'vector_sim:';
    this.cacheTTL = 300; // 5 minutes
  }

  /**
   * Find users with similar personality vectors
   * @param {string} userId - Target user ID
   * @param {Object} options - Search options
   * @returns {Array} Similar users with similarity scores
   */
  async findSimilarUsers(userId, options = {}) {
    const {
      threshold = 0.7,
      limit = 10,
      includeScores = true
    } = options;

    const cacheKey = `${this.cachePrefix}users:${userId}:${threshold}:${limit}`;

    try {
      // Check cache first
      if (this.redis) {
        const cached = await this.redis.get(cacheKey);
        if (cached) {
          log.debug('Vector similarity cache hit', { userId, cacheKey });
          return JSON.parse(cached);
        }
      }

      // Query database using vector similarity function
      const query = `
        SELECT * FROM find_similar_users($1, $2, $3)
      `;

      const result = await pool.query(query, [userId, threshold, limit]);

      const similarUsers = result.rows.map(row => ({
        userId: row.user_id,
        similarities: {
          cognitive: parseFloat(row.cognitive_similarity),
          emotional: parseFloat(row.emotional_similarity),
          aesthetic: parseFloat(row.aesthetic_similarity),
          overall: parseFloat(row.overall_similarity)
        },
        matchType: this.categorizeMatch(row)
      }));

      // Cache the results
      if (this.redis && similarUsers.length > 0) {
        await this.redis.setex(cacheKey, this.cacheTTL, JSON.stringify(similarUsers));
      }

      log.info('Vector similarity search completed', {
        userId,
        foundUsers: similarUsers.length,
        avgSimilarity: similarUsers.length > 0
          ? (similarUsers.reduce((sum, u) => sum + u.similarities.overall, 0) / similarUsers.length).toFixed(3)
          : 0
      });

      return similarUsers;

    } catch (error) {
      log.error('Vector similarity search failed', error, { userId, options });
      throw new Error('Failed to find similar users');
    }
  }

  /**
   * Find artworks similar to a given text or embedding
   * @param {string} searchText - Text to search for
   * @param {Array} embedding - Optional pre-computed embedding
   * @param {Object} options - Search options
   * @returns {Array} Similar artworks
   */
  async findSimilarArtworks(searchText, embedding = null, options = {}) {
    const {
      threshold = 0.7,
      limit = 20
    } = options;

    const cacheKey = `${this.cachePrefix}artworks:${Buffer.from(searchText).toString('base64')}:${threshold}:${limit}`;

    try {
      // Check cache first
      if (this.redis) {
        const cached = await this.redis.get(cacheKey);
        if (cached) {
          log.debug('Artwork similarity cache hit', { searchText: searchText.substring(0, 50) });
          return JSON.parse(cached);
        }
      }

      // Generate embedding if not provided
      let searchEmbedding = embedding;
      if (!searchEmbedding) {
        searchEmbedding = await openaiService.generateEmbedding(searchText);
      }

      // Convert array to vector format for PostgreSQL
      const vectorString = `[${searchEmbedding.join(',')}]`;

      const query = `
        SELECT * FROM find_similar_artworks($1, $2::vector, $3, $4)
      `;

      const result = await pool.query(query, [searchText, vectorString, threshold, limit]);

      const similarArtworks = result.rows.map(row => ({
        artworkId: row.artwork_id,
        title: row.title,
        artist: row.artist_name,
        similarityScore: parseFloat(row.similarity_score),
        relevanceCategory: this.categorizeRelevance(row.similarity_score)
      }));

      // Cache the results
      if (this.redis && similarArtworks.length > 0) {
        await this.redis.setex(cacheKey, this.cacheTTL, JSON.stringify(similarArtworks));
      }

      log.info('Artwork similarity search completed', {
        searchText: searchText.substring(0, 50),
        foundArtworks: similarArtworks.length,
        avgSimilarity: similarArtworks.length > 0
          ? (similarArtworks.reduce((sum, a) => sum + a.similarityScore, 0) / similarArtworks.length).toFixed(3)
          : 0
      });

      return similarArtworks;

    } catch (error) {
      log.error('Artwork similarity search failed', error, { searchText: searchText.substring(0, 50) });
      throw new Error('Failed to find similar artworks');
    }
  }

  /**
   * Update user's personality vectors
   * @param {string} userId - User ID
   * @param {Object} vectors - Vector data
   * @returns {boolean} Success status
   */
  async updateUserVectors(userId, vectors) {
    const { cognitive, emotional, aesthetic } = vectors;

    try {
      // Convert arrays to vector format
      const cognitiveVector = `[${cognitive.join(',')}]`;
      const emotionalVector = `[${emotional.join(',')}]`;
      const aestheticVector = `[${aesthetic.join(',')}]`;

      const query = `
        SELECT update_user_vectors($1, $2::vector, $3::vector, $4::vector)
      `;

      const result = await pool.query(query, [
        userId,
        cognitiveVector,
        emotionalVector,
        aestheticVector
      ]);

      const success = result.rows[0].update_user_vectors;

      if (success) {
        // Invalidate cache for this user
        if (this.redis) {
          const pattern = `${this.cachePrefix}users:${userId}:*`;
          const keys = await this.redis.keys(pattern);
          if (keys.length > 0) {
            await this.redis.del(...keys);
          }
        }

        log.info('User vectors updated successfully', { userId });
      }

      return success;

    } catch (error) {
      log.error('Failed to update user vectors', error, { userId });
      throw new Error('Failed to update user vectors');
    }
  }

  /**
   * Get vector statistics and health metrics
   * @returns {Object} Vector system statistics
   */
  async getVectorStats() {
    try {
      const query = `SELECT * FROM vector_stats`;
      const result = await pool.query(query);

      const stats = {
        tables: result.rows,
        timestamp: new Date().toISOString(),
        cacheStatus: this.redis ? 'connected' : 'disconnected'
      };

      log.debug('Vector stats retrieved', stats);
      return stats;

    } catch (error) {
      log.error('Failed to get vector stats', error);
      throw new Error('Failed to get vector statistics');
    }
  }

  /**
   * Semantic search across all content
   * @param {string} query - Search query
   * @param {Object} options - Search options
   * @returns {Object} Search results from multiple sources
   */
  async semanticSearch(query, options = {}) {
    const { limit = 10, threshold = 0.6 } = options;

    try {
      // Generate embedding for the search query
      const queryEmbedding = await openaiService.generateEmbedding(query);

      // Search in parallel across different content types
      const [artworks, users] = await Promise.all([
        this.findSimilarArtworks(query, queryEmbedding, { limit, threshold }),
        // Could add user search based on profile text
        Promise.resolve([]) // Placeholder for user search
      ]);

      const results = {
        query,
        results: {
          artworks: artworks.slice(0, limit),
          users: users.slice(0, limit)
        },
        metadata: {
          totalResults: artworks.length + users.length,
          searchTime: Date.now(),
          threshold
        }
      };

      log.info('Semantic search completed', {
        query: query.substring(0, 50),
        artworkResults: artworks.length,
        userResults: users.length
      });

      return results;

    } catch (error) {
      log.error('Semantic search failed', error, { query: query.substring(0, 50) });
      throw new Error('Semantic search failed');
    }
  }

  /**
   * Categorize user match type based on similarity scores
   * @private
   */
  categorizeMatch(row) {
    const { cognitive_similarity, emotional_similarity, aesthetic_similarity } = row;

    if (cognitive_similarity > 0.9) return 'cognitive_twin';
    if (emotional_similarity > 0.9) return 'emotional_match';
    if (aesthetic_similarity > 0.9) return 'aesthetic_soulmate';
    if (row.overall_similarity > 0.85) return 'strong_match';
    if (row.overall_similarity > 0.75) return 'good_match';
    return 'potential_match';
  }

  /**
   * Categorize artwork relevance based on similarity score
   * @private
   */
  categorizeRelevance(score) {
    if (score > 0.9) return 'highly_relevant';
    if (score > 0.8) return 'very_relevant';
    if (score > 0.7) return 'relevant';
    if (score > 0.6) return 'somewhat_relevant';
    return 'loosely_relevant';
  }

  /**
   * Clear vector similarity cache
   * @param {string} pattern - Cache key pattern to clear
   */
  async clearCache(pattern = '*') {
    if (!this.redis) return false;

    try {
      const fullPattern = `${this.cachePrefix}${pattern}`;
      const keys = await this.redis.keys(fullPattern);

      if (keys.length > 0) {
        await this.redis.del(...keys);
        log.info('Vector similarity cache cleared', { pattern: fullPattern, keysCleared: keys.length });
      }

      return true;
    } catch (error) {
      log.error('Failed to clear vector cache', error, { pattern });
      return false;
    }
  }
}

module.exports = new VectorSimilarityService();

const { pool } = require('../config/database');
const logger = require('../utils/logger');

class VillageService {
  constructor() {
    // Personality type to village cluster mapping
    this.personalityToVillage = {
      // ğŸ›ï¸ Contemplative Sanctuary (í˜¼ìì„œ ê¹Šì´ ì‚¬ìƒ‰)
      'LAEF': 'CONTEMPLATIVE',
      'LAMF': 'CONTEMPLATIVE',
      'LREF': 'CONTEMPLATIVE',
      'LRMF': 'CONTEMPLATIVE',

      // ğŸ“š Academic Forum (ë…¼ë¦¬ì™€ ì²´ê³„ë¡œ íƒêµ¬)
      'LRMC': 'ACADEMIC',
      'LREC': 'ACADEMIC',
      'SRMC': 'ACADEMIC',
      'SREC': 'ACADEMIC',

      // ğŸ­ Social Gallery (í•¨ê»˜ ê°ìƒí•˜ê³  ë‚˜ëˆ”)
      'SAEF': 'SOCIAL',
      'SAEC': 'SOCIAL',
      'SREF': 'SOCIAL',
      'SREC': 'SOCIAL',

      // âœ¨ Creative Studio (ê°ì„±ê³¼ ì˜ê°ì´ í˜ëŸ¬ë„˜ì¹¨)
      'LAMC': 'CREATIVE',
      'LAMF': 'CREATIVE',
      'SAMC': 'CREATIVE',
      'SAMF': 'CREATIVE'
    };
  }

  // Get village cluster for personality type
  getVillageForPersonality(personalityType) {
    return this.personalityToVillage[personalityType] || 'CONTEMPLATIVE';
  }

  // Get village information
  async getVillageInfo(villageCode) {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          SELECT 
            village_code,
            name,
            korean_name,
            description,
            theme,
            community_features,
            village_perks,
            member_count,
            is_active
          FROM villages
          WHERE village_code = $1
        `, [villageCode]);

        if (result.rows.length === 0) {
          return null;
        }

        const village = result.rows[0];

        // Get personality types in this village
        const personalityResult = await client.query(`
          SELECT 
            personality_type,
            cluster_reason,
            art_viewing_style
          FROM personality_village_mapping
          WHERE village_code = $1
        `, [villageCode]);

        village.personalities = personalityResult.rows;

        return village;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting village info:', error);
      throw error;
    }
  }

  // Get village members
  async getVillageMembers(villageCode, limit = 50) {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          SELECT 
            u.id,
            u.nickname,
            u.current_identity_type,
            u.evolution_stage,
            u.evolution_points,
            up.archetype_name,
            up.generated_image_url,
            vm.joined_at,
            vm.contribution_points,
            vm.badges_earned,
            vm.role
          FROM village_memberships vm
          JOIN users u ON vm.user_id = u.id
          LEFT JOIN user_profiles up ON u.id = up.user_id
          WHERE vm.village_code = $1 
            AND vm.status = 'active'
          ORDER BY vm.contribution_points DESC, vm.joined_at ASC
          LIMIT $2
        `, [villageCode, limit]);

        return result.rows;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting village members:', error);
      throw error;
    }
  }

  // Get user's current village
  async getUserVillage(userId) {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          SELECT 
            vm.village_code,
            v.name,
            v.korean_name,
            vm.joined_at,
            vm.contribution_points,
            vm.badges_earned,
            vm.role
          FROM village_memberships vm
          JOIN villages v ON vm.village_code = v.village_code
          WHERE vm.user_id = $1 AND vm.status = 'active'
        `, [userId]);

        return result.rows[0] || null;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting user village:', error);
      throw error;
    }
  }

  // Join village (when identity changes)
  async joinVillage(userId, personalityType) {
    try {
      const villageCode = this.getVillageForPersonality(personalityType);

      const client = await pool.connect();
      try {
        await client.query('BEGIN');

        // End previous membership
        await client.query(`
          UPDATE village_memberships 
          SET status = 'alumni', left_at = NOW()
          WHERE user_id = $1 AND status = 'active'
        `, [userId]);

        // Create new membership
        await client.query(`
          INSERT INTO village_memberships (user_id, village_code, status)
          VALUES ($1, $2, 'active')
          ON CONFLICT (user_id, village_code, status) DO NOTHING
        `, [userId, villageCode]);

        // Update village member count
        await client.query(`
          UPDATE villages 
          SET member_count = (
            SELECT COUNT(*) 
            FROM village_memberships 
            WHERE village_code = $1 AND status = 'active'
          )
          WHERE village_code = $1
        `, [villageCode]);

        await client.query('COMMIT');

        logger.info(`User ${userId} joined village ${villageCode} with personality ${personalityType}`);

        return { villageCode, personalityType };
      } catch (error) {
        await client.query('ROLLBACK');
        throw error;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error joining village:', error);
      throw error;
    }
  }

  // Add contribution points
  async addContributionPoints(userId, points, reason) {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          UPDATE village_memberships 
          SET contribution_points = contribution_points + $1
          WHERE user_id = $2 AND status = 'active'
          RETURNING village_code, contribution_points
        `, [points, userId]);

        if (result.rows.length > 0) {
          logger.info(`Added ${points} contribution points to user ${userId} for ${reason}`);
          return result.rows[0];
        }

        return null;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error adding contribution points:', error);
      throw error;
    }
  }

  // Get village events
  async getVillageEvents(villageCode) {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          SELECT 
            id,
            event_name,
            event_type,
            description,
            start_time,
            end_time,
            max_participants,
            current_participants,
            rewards
          FROM village_events
          WHERE village_code = $1 
            AND start_time > NOW()
          ORDER BY start_time ASC
        `, [villageCode]);

        return result.rows;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting village events:', error);
      throw error;
    }
  }

  // Get village statistics
  async getVillageStats() {
    try {
      const client = await pool.connect();
      try {
        const result = await client.query(`
          SELECT 
            v.village_code,
            v.name,
            v.korean_name,
            COUNT(vm.user_id) as member_count,
            AVG(vm.contribution_points) as avg_contribution,
            MAX(vm.contribution_points) as max_contribution
          FROM villages v
          LEFT JOIN village_memberships vm ON v.village_code = vm.village_code 
            AND vm.status = 'active'
          GROUP BY v.village_code, v.name, v.korean_name
          ORDER BY member_count DESC
        `);

        return result.rows;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting village stats:', error);
      throw error;
    }
  }

  // Award village badge
  async awardVillageBadge(userId, badgeName, reason) {
    try {
      const client = await pool.connect();
      try {
        // Get current badges
        const result = await client.query(`
          SELECT badges_earned 
          FROM village_memberships 
          WHERE user_id = $1 AND status = 'active'
        `, [userId]);

        if (result.rows.length === 0) {
          return null;
        }

        const currentBadges = result.rows[0].badges_earned || [];

        if (!currentBadges.includes(badgeName)) {
          const newBadges = [...currentBadges, badgeName];

          await client.query(`
            UPDATE village_memberships 
            SET badges_earned = $1
            WHERE user_id = $2 AND status = 'active'
          `, [newBadges, userId]);

          logger.info(`Awarded badge '${badgeName}' to user ${userId} for ${reason}`);
          return { badgeName, reason, totalBadges: newBadges.length };
        }

        return null;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error awarding village badge:', error);
      throw error;
    }
  }

  // Get nearby members (for card exchange)
  async getNearbyMembers(userId, villageCode, limit = 10) {
    try {
      const client = await pool.connect();
      try {
        // For now, just get random active members from the same village
        const result = await client.query(`
          SELECT 
            u.id,
            u.nickname,
            u.current_identity_type,
            u.evolution_stage,
            up.archetype_name,
            vm.contribution_points,
            vm.badges_earned,
            CASE 
              WHEN RANDOM() < 0.3 THEN 'online'
              WHEN RANDOM() < 0.7 THEN 'away'
              ELSE 'offline'
            END as status
          FROM village_memberships vm
          JOIN users u ON vm.user_id = u.id
          LEFT JOIN user_profiles up ON u.id = up.user_id
          WHERE vm.village_code = $1 
            AND vm.status = 'active'
            AND u.id != $2
          ORDER BY RANDOM()
          LIMIT $3
        `, [villageCode, userId, limit]);

        return result.rows;
      } finally {
        client.release();
      }
    } catch (error) {
      logger.error('Error getting nearby members:', error);
      throw error;
    }
  }
}

