const { createClient } = require('@supabase/supabase-js');
const fs = require('fs').promises;
const path = require('path');
const { log } = require('../config/logger');

class DatabaseBackup {
  constructor() {
    this.supabase = createClient(
      process.env.SUPABASE_URL,
      process.env.SUPABASE_SERVICE_KEY
    );
    this.backupDir = path.join(__dirname, '../../backups');
  }

  async ensureBackupDirectory() {
    try {
      await fs.mkdir(this.backupDir, { recursive: true });
    } catch (error) {
      if (error.code !== 'EEXIST') {
        throw error;
      }
    }
  }

  async backupExhibitionData() {
    try {
      await this.ensureBackupDirectory();

      const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
      const filename = `exhibition-backup-${timestamp}.json`;
      const filepath = path.join(this.backupDir, filename);

      log.info('Starting exhibition data backup...');

      // Backup exhibitions
      const { data: exhibitions, error: exhibitionsError } = await this.supabase
        .from('exhibitions')
        .select('*');

      if (exhibitionsError) {
        throw new Error(`Failed to backup exhibitions: ${exhibitionsError.message}`);
      }

      // Backup venues
      const { data: venues, error: venuesError } = await this.supabase
        .from('venues')
        .select('*');

      if (venuesError) {
        throw new Error(`Failed to backup venues: ${venuesError.message}`);
      }

      // Backup exhibition submissions
      const { data: submissions, error: submissionsError } = await this.supabase
        .from('exhibition_submissions')
        .select('*');

      if (submissionsError) {
        throw new Error(`Failed to backup submissions: ${submissionsError.message}`);
      }

      // Backup exhibition likes
      const { data: likes, error: likesError } = await this.supabase
        .from('exhibition_likes')
        .select('*');

      if (likesError) {
        throw new Error(`Failed to backup likes: ${likesError.message}`);
      }

      // Backup exhibition views
      const { data: views, error: viewsError } = await this.supabase
        .from('exhibition_views')
        .select('*');

      if (viewsError) {
        throw new Error(`Failed to backup views: ${viewsError.message}`);
      }

      const backupData = {
        timestamp: new Date().toISOString(),
        version: '1.0',
        tables: {
          exhibitions: {
            count: exhibitions.length,
            data: exhibitions
          },
          venues: {
            count: venues.length,
            data: venues
          },
          exhibition_submissions: {
            count: submissions.length,
            data: submissions
          },
          exhibition_likes: {
            count: likes.length,
            data: likes
          },
          exhibition_views: {
            count: views.length,
            data: views
          }
        }
      };

      await fs.writeFile(filepath, JSON.stringify(backupData, null, 2));

      log.info('Exhibition data backup completed', {
        filename,
        exhibitionsCount: exhibitions.length,
        venuesCount: venues.length,
        submissionsCount: submissions.length,
        likesCount: likes.length,
        viewsCount: views.length
      });

      return { success: true, filename, filepath };

    } catch (error) {
      log.error('Database backup failed', { error: error.message });
      throw error;
    }
  }

  async restoreExhibitionData(backupPath) {
    try {
      const backupData = JSON.parse(await fs.readFile(backupPath, 'utf8'));

      log.info('Starting exhibition data restore...');

      // Restore venues first (foreign key dependency)
      if (backupData.tables.venues.data.length > 0) {
        const { error: venuesError } = await this.supabase
          .from('venues')
          .upsert(backupData.tables.venues.data);

        if (venuesError) {
          throw new Error(`Failed to restore venues: ${venuesError.message}`);
        }
      }

      // Restore exhibitions
      if (backupData.tables.exhibitions.data.length > 0) {
        const { error: exhibitionsError } = await this.supabase
          .from('exhibitions')
          .upsert(backupData.tables.exhibitions.data);

        if (exhibitionsError) {
          throw new Error(`Failed to restore exhibitions: ${exhibitionsError.message}`);
        }
      }

      // Restore submissions
      if (backupData.tables.exhibition_submissions.data.length > 0) {
        const { error: submissionsError } = await this.supabase
          .from('exhibition_submissions')
          .upsert(backupData.tables.exhibition_submissions.data);

        if (submissionsError) {
          throw new Error(`Failed to restore submissions: ${submissionsError.message}`);
        }
      }

      // Restore likes
      if (backupData.tables.exhibition_likes.data.length > 0) {
        const { error: likesError } = await this.supabase
          .from('exhibition_likes')
          .upsert(backupData.tables.exhibition_likes.data);

        if (likesError) {
          throw new Error(`Failed to restore likes: ${likesError.message}`);
        }
      }

      // Restore views
      if (backupData.tables.exhibition_views.data.length > 0) {
        const { error: viewsError } = await this.supabase
          .from('exhibition_views')
          .upsert(backupData.tables.exhibition_views.data);

        if (viewsError) {
          throw new Error(`Failed to restore views: ${viewsError.message}`);
        }
      }

      log.info('Exhibition data restore completed');
      return { success: true };

    } catch (error) {
      log.error('Database restore failed', { error: error.message });
      throw error;
    }
  }

  async listBackups() {
    try {
      await this.ensureBackupDirectory();
      const files = await fs.readdir(this.backupDir);

      const backups = files
        .filter(file => file.startsWith('exhibition-backup-') && file.endsWith('.json'))
        .map(file => {
          const filepath = path.join(this.backupDir, file);
          return {
            filename: file,
            filepath,
            timestamp: file.replace('exhibition-backup-', '').replace('.json', '')
          };
        })
        .sort((a, b) => b.timestamp.localeCompare(a.timestamp));

      return backups;
    } catch (error) {
      log.error('Failed to list backups', { error: error.message });
      throw error;
    }
  }

  async cleanupOldBackups(keepDays = 30) {
    try {
      const backups = await this.listBackups();
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - keepDays);

      const toDelete = backups.filter(backup => {
        const backupDate = new Date(backup.timestamp.replace(/-/g, ':'));
        return backupDate < cutoffDate;
      });

      for (const backup of toDelete) {
        await fs.unlink(backup.filepath);
        log.info('Deleted old backup', { filename: backup.filename });
      }

      return { deletedCount: toDelete.length };
    } catch (error) {
      log.error('Failed to cleanup old backups', { error: error.message });
      throw error;
    }
  }

  async scheduleBackup() {
    // Schedule daily backup at 2 AM
    const scheduleNextBackup = () => {
      const now = new Date();
      const nextBackup = new Date();
      nextBackup.setDate(now.getDate() + 1);
      nextBackup.setHours(2, 0, 0, 0);

      const timeUntilNext = nextBackup.getTime() - now.getTime();

      setTimeout(async () => {
        try {
          await this.backupExhibitionData();
          await this.cleanupOldBackups(30); // Keep 30 days
          scheduleNextBackup(); // Schedule next backup
        } catch (error) {
          log.error('Scheduled backup failed', { error: error.message });
          scheduleNextBackup(); // Continue scheduling even if backup fails
        }
      }, timeUntilNext);

      log.info('Next backup scheduled', { nextBackup: nextBackup.toISOString() });
    };

    scheduleNextBackup();
  }
}

module.exports = DatabaseBackup;

/**
 * SAYU Memory Optimizer
 * Node.js ë©”ëª¨ë¦¬ ëˆ„ìˆ˜ ë°©ì§€ ë° ìµœì í™” ìœ í‹¸ë¦¬í‹°
 */

const EventEmitter = require('events');
const { performance } = require('perf_hooks');

class MemoryOptimizer extends EventEmitter {
  constructor(options = {}) {
    super();

    this.options = {
      // ë©”ëª¨ë¦¬ ì„ê³„ê°’ (MB)
      maxMemoryMB: options.maxMemoryMB || 2048,
      warningThresholdMB: options.warningThresholdMB || 1536,

      // ëª¨ë‹ˆí„°ë§ ê°„ê²©
      monitorInterval: options.monitorInterval || 30000, // 30ì´ˆ

      // GC ê°•ì œ ì‹¤í–‰ ì„ê³„ê°’
      forceGCThresholdMB: options.forceGCThresholdMB || 1800,

      // ë©”ëª¨ë¦¬ ì •ë¦¬ ì˜µì…˜
      enableAutoCleanup: options.enableAutoCleanup !== false,
      enableHeapSnapshot: options.enableHeapSnapshot || false,

      // ë¡œê¹…
      enableLogging: options.enableLogging !== false,
      logLevel: options.logLevel || 'info'
    };

    // ëª¨ë‹ˆí„°ë§ ìƒíƒœ
    this.isMonitoring = false;
    this.monitorTimer = null;
    this.lastGCTime = Date.now();
    this.memoryHistory = [];
    this.maxHistorySize = 100;

    // ë©”ëª¨ë¦¬ ì •ë¦¬ ìºì‹œ
    this.cleanupTasks = new Map();
    this.intervalTasks = new Map();
    this.eventListeners = new Map();

    // ì„±ëŠ¥ ëª¨ë‹ˆí„°ë§
    this.performanceMarks = new Map();

    this.log('MemoryOptimizer initialized', 'info');
  }

  /**
   * ë©”ëª¨ë¦¬ ëª¨ë‹ˆí„°ë§ ì‹œì‘
   */
  startMonitoring() {
    if (this.isMonitoring) {
      this.log('Memory monitoring already started', 'warn');
      return;
    }

    this.isMonitoring = true;
    this.monitorTimer = setInterval(() => {
      this.checkMemoryUsage();
    }, this.options.monitorInterval);

    // í”„ë¡œì„¸ìŠ¤ ì¢…ë£Œ ì‹œ ì •ë¦¬
    process.on('exit', () => this.cleanup());
    process.on('SIGINT', () => this.gracefulShutdown());
    process.on('SIGTERM', () => this.gracefulShutdown());
    process.on('uncaughtException', (error) => this.handleError('uncaughtException', error));
    process.on('unhandledRejection', (reason, promise) => this.handleError('unhandledRejection', { reason, promise }));

    this.log('Memory monitoring started', 'info');
    this.emit('monitoring:started');
  }

  /**
   * ë©”ëª¨ë¦¬ ì‚¬ìš©ëŸ‰ í™•ì¸
   */
  checkMemoryUsage() {
    const memUsage = process.memoryUsage();
    const cpuUsage = process.cpuUsage();

    // MB ë‹¨ìœ„ë¡œ ë³€í™˜
    const memoryStats = {
      rss: Math.round(memUsage.rss / 1024 / 1024),
      heapUsed: Math.round(memUsage.heapUsed / 1024 / 1024),
      heapTotal: Math.round(memUsage.heapTotal / 1024 / 1024),
      external: Math.round(memUsage.external / 1024 / 1024),
      arrayBuffers: Math.round(memUsage.arrayBuffers / 1024 / 1024),
      timestamp: Date.now(),
      uptime: Math.round(process.uptime())
    };

    // CPU ì‚¬ìš©ë¥  ê³„ì‚°
    const cpuPercent = this.calculateCPUUsage(cpuUsage);

    // íˆìŠ¤í† ë¦¬ ì—…ë°ì´íŠ¸
    this.updateMemoryHistory(memoryStats);

    // ë©”ëª¨ë¦¬ ìƒíƒœ í‰ê°€
    this.evaluateMemoryStatus(memoryStats);

    // ì´ë²¤íŠ¸ ë°œìƒ
    this.emit('memory:check', { memory: memoryStats, cpu: cpuPercent });

    return { memory: memoryStats, cpu: cpuPercent };
  }

  /**
   * CPU ì‚¬ìš©ë¥  ê³„ì‚°
   */
  calculateCPUUsage(cpuUsage) {
    if (!this.lastCPUUsage) {
      this.lastCPUUsage = cpuUsage;
      return 0;
    }

    const userDiff = cpuUsage.user - this.lastCPUUsage.user;
    const systemDiff = cpuUsage.system - this.lastCPUUsage.system;
    const totalDiff = userDiff + systemDiff;

    this.lastCPUUsage = cpuUsage;

    // ë§ˆì´í¬ë¡œì´ˆë¥¼ í¼ì„¼íŠ¸ë¡œ ë³€í™˜
    return Math.round((totalDiff / 1000000) * 100);
  }

  /**
   * ë©”ëª¨ë¦¬ íˆìŠ¤í† ë¦¬ ì—…ë°ì´íŠ¸
   */
  updateMemoryHistory(stats) {
    this.memoryHistory.push(stats);

    // íˆìŠ¤í† ë¦¬ í¬ê¸° ì œí•œ
    if (this.memoryHistory.length > this.maxHistorySize) {
      this.memoryHistory.shift();
    }
  }

  /**
   * ë©”ëª¨ë¦¬ ìƒíƒœ í‰ê°€ ë° ëŒ€ì‘
   */
  evaluateMemoryStatus(stats) {
    const { heapUsed, rss } = stats;

    // ê²½ê³  ì„ê³„ê°’ í™•ì¸
    if (heapUsed > this.options.warningThresholdMB || rss > this.options.warningThresholdMB) {
      this.log(`Memory warning - Heap: ${heapUsed}MB, RSS: ${rss}MB`, 'warn');
      this.emit('memory:warning', stats);

      if (this.options.enableAutoCleanup) {
        this.performCleanup('warning');
      }
    }

    // ê°•ì œ GC ì„ê³„ê°’ í™•ì¸
    if (heapUsed > this.options.forceGCThresholdMB || rss > this.options.forceGCThresholdMB) {
      this.log(`Memory critical - forcing GC - Heap: ${heapUsed}MB, RSS: ${rss}MB`, 'error');
      this.emit('memory:critical', stats);

      this.forceGarbageCollection();

      if (this.options.enableAutoCleanup) {
        this.performCleanup('critical');
      }
    }

    // ìµœëŒ€ ë©”ëª¨ë¦¬ ì´ˆê³¼ í™•ì¸
    if (heapUsed > this.options.maxMemoryMB || rss > this.options.maxMemoryMB) {
      this.log(`Memory limit exceeded - Heap: ${heapUsed}MB, RSS: ${rss}MB`, 'error');
      this.emit('memory:limit', stats);

      // ê¸´ê¸‰ ì •ë¦¬
      this.performEmergencyCleanup();
    }
  }

  /**
   * ê°€ë¹„ì§€ ì»¬ë ‰ì…˜ ê°•ì œ ì‹¤í–‰
   */
  forceGarbageCollection() {
    const now = Date.now();

    // ë§ˆì§€ë§‰ GC í›„ ìµœì†Œ ì‹œê°„ ê²½ê³¼ í™•ì¸ (5ì´ˆ)
    if (now - this.lastGCTime < 5000) {
      return false;
    }

    if (global.gc) {
      const beforeGC = process.memoryUsage();

      try {
        global.gc();
        this.lastGCTime = now;

        const afterGC = process.memoryUsage();
        const freed = Math.round((beforeGC.heapUsed - afterGC.heapUsed) / 1024 / 1024);

        this.log(`Garbage collection completed - Freed: ${freed}MB`, 'info');
        this.emit('gc:completed', { before: beforeGC, after: afterGC, freed });

        return true;
      } catch (error) {
        this.log(`Garbage collection failed: ${error.message}`, 'error');
        return false;
      }
    } else {
      this.log('Garbage collection not available (use --expose-gc flag)', 'warn');
      return false;
    }
  }

  /**
   * ë©”ëª¨ë¦¬ ì •ë¦¬ ìˆ˜í–‰
   */
  performCleanup(level = 'normal') {
    this.log(`Performing ${level} memory cleanup`, 'info');

    try {
      // 1. ìºì‹œ ì •ë¦¬
      this.cleanupCaches();

      // 2. ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì •ë¦¬
      this.cleanupEventListeners();

      // 3. íƒ€ì´ë¨¸ ì •ë¦¬
      this.cleanupTimers();

      // 4. í° ê°ì²´ í•´ì œ
      if (level === 'critical' || level === 'emergency') {
        this.cleanupLargeObjects();
      }

      // 5. Node.js ë‚´ë¶€ ì •ë¦¬
      if (level === 'emergency') {
        this.performEmergencyOperations();
      }

      // ê°€ë¹„ì§€ ì»¬ë ‰ì…˜ ì‹¤í–‰
      this.forceGarbageCollection();

      this.emit('cleanup:completed', { level });

    } catch (error) {
      this.log(`Cleanup failed: ${error.message}`, 'error');
      this.emit('cleanup:failed', { level, error });
    }
  }

  /**
   * ìºì‹œ ì •ë¦¬
   */
  cleanupCaches() {
    // Express ìºì‹œ ì •ë¦¬
    if (global.app && global.app.cache) {
      global.app.cache.clear();
    }

    // require ìºì‹œ ì¤‘ ì¼ë¶€ ì •ë¦¬ (ìœ„í—˜í•˜ë¯€ë¡œ ì‹ ì¤‘í•˜ê²Œ)
    const modulesToClean = Object.keys(require.cache).filter(module =>
      module.includes('node_modules/lodash') ||
      module.includes('node_modules/moment') ||
      module.includes('temp_')
    );

    modulesToClean.forEach(module => {
      delete require.cache[module];
    });

    this.log(`Cleaned ${modulesToClean.length} cached modules`, 'debug');
  }

  /**
   * ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì •ë¦¬
   */
  cleanupEventListeners() {
    // ë“±ë¡ëœ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì •ë¦¬
    this.eventListeners.forEach((listeners, emitter) => {
      listeners.forEach(({ event, listener }) => {
        try {
          emitter.removeListener(event, listener);
        } catch (error) {
          // ì´ë¯¸ ì œê±°ëœ ë¦¬ìŠ¤ë„ˆ ë¬´ì‹œ
        }
      });
    });

    this.eventListeners.clear();
  }

  /**
   * íƒ€ì´ë¨¸ ì •ë¦¬
   */
  cleanupTimers() {
    // ë“±ë¡ëœ íƒ€ì´ë¨¸ ì •ë¦¬
    this.intervalTasks.forEach((timer, name) => {
      clearInterval(timer);
    });
    this.intervalTasks.clear();

    // ì •ë¦¬ ì‘ì—… ìˆ˜í–‰
    this.cleanupTasks.forEach((task, name) => {
      try {
        if (typeof task === 'function') {
          task();
        }
      } catch (error) {
        this.log(`Cleanup task '${name}' failed: ${error.message}`, 'warn');
      }
    });
  }

  /**
   * í° ê°ì²´ ì •ë¦¬
   */
  cleanupLargeObjects() {
    // ì „ì—­ ë³€ìˆ˜ì—ì„œ í° ê°ì²´ ì°¾ì•„ì„œ ì •ë¦¬
    const globalKeys = Object.keys(global);

    globalKeys.forEach(key => {
      const obj = global[key];

      if (obj && typeof obj === 'object') {
        // ë°°ì—´ì´ë‚˜ ê°ì²´ê°€ í¬ë©´ ì •ë¦¬
        if (Array.isArray(obj) && obj.length > 1000) {
          obj.length = 0;
          this.log(`Cleared large array: ${key}`, 'debug');
        } else if (obj.constructor === Object && Object.keys(obj).length > 1000) {
          Object.keys(obj).forEach(k => delete obj[k]);
          this.log(`Cleared large object: ${key}`, 'debug');
        }
      }
    });
  }

  /**
   * ê¸´ê¸‰ ì •ë¦¬ ì‘ì—…
   */
  performEmergencyCleanup() {
    this.log('Performing emergency cleanup', 'error');

    try {
      // 1. ëª¨ë“  ì •ë¦¬ ì‘ì—… ìˆ˜í–‰
      this.performCleanup('emergency');

      // 2. ì—°ê²°ëœ ì†Œì¼“ ì •ë¦¬
      if (global.server && global.server.connections) {
        global.server.connections.forEach(socket => {
          if (!socket.destroyed) {
            socket.destroy();
          }
        });
      }

      // 3. ë°ì´í„°ë² ì´ìŠ¤ ì—°ê²° ì •ë¦¬
      if (global.db && typeof global.db.end === 'function') {
        global.db.end();
      }

      // 4. Redis ì—°ê²° ì •ë¦¬
      if (global.redis && typeof global.redis.disconnect === 'function') {
        global.redis.disconnect();
      }

      // 5. ë§ˆì§€ë§‰ ìˆ˜ë‹¨: í”„ë¡œì„¸ìŠ¤ ì¬ì‹œì‘ ê¶Œê³ 
      this.emit('memory:emergency', {
        message: 'Emergency cleanup completed. Consider restarting the process.',
        memoryUsage: process.memoryUsage()
      });

    } catch (error) {
      this.log(`Emergency cleanup failed: ${error.message}`, 'error');

      // ë³µêµ¬ ë¶ˆê°€ëŠ¥í•œ ìƒí™©
      this.emit('memory:fatal', {
        message: 'Fatal memory error. Process restart required.',
        error: error.message
      });
    }
  }

  /**
   * ê¸´ê¸‰ ì‘ì—… ìˆ˜í–‰
   */
  performEmergencyOperations() {
    // Buffer í’€ ì •ë¦¬
    if (Buffer.poolSize) {
      Buffer.poolSize = 8192; // ê¸°ë³¸ê°’ìœ¼ë¡œ ë¦¬ì…‹
    }

    // UV ìŠ¤ë ˆë“œ í’€ ìµœì í™”
    process.env.UV_THREADPOOL_SIZE = '4';

    // V8 í”Œë˜ê·¸ ë™ì  ì¡°ì •
    if (global.gc) {
      // ë” ì ê·¹ì ì¸ GC ì„¤ì •
      process.nextTick(() => {
        global.gc();
        global.gc();
      });
    }
  }

  /**
   * ì •ë¦¬ ì‘ì—… ë“±ë¡
   */
  registerCleanupTask(name, task) {
    this.cleanupTasks.set(name, task);
  }

  /**
   * ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡ (ìë™ ì •ë¦¬ìš©)
   */
  registerEventListener(emitter, event, listener) {
    if (!this.eventListeners.has(emitter)) {
      this.eventListeners.set(emitter, []);
    }

    this.eventListeners.get(emitter).push({ event, listener });
    emitter.on(event, listener);
  }

  /**
   * ì¸í„°ë²Œ ë“±ë¡ (ìë™ ì •ë¦¬ìš©)
   */
  registerInterval(name, callback, interval) {
    const timer = setInterval(callback, interval);
    this.intervalTasks.set(name, timer);
    return timer;
  }

  /**
   * ì„±ëŠ¥ ì¸¡ì • ì‹œì‘
   */
  startPerformanceMark(name) {
    this.performanceMarks.set(name, performance.now());
  }

  /**
   * ì„±ëŠ¥ ì¸¡ì • ì¢…ë£Œ
   */
  endPerformanceMark(name) {
    const startTime = this.performanceMarks.get(name);
    if (startTime) {
      const duration = performance.now() - startTime;
      this.performanceMarks.delete(name);
      return duration;
    }
    return null;
  }

  /**
   * ë©”ëª¨ë¦¬ ë³´ê³ ì„œ ìƒì„±
   */
  generateMemoryReport() {
    const currentMemory = process.memoryUsage();
    const uptime = process.uptime();

    // ë©”ëª¨ë¦¬ íŠ¸ë Œë“œ ë¶„ì„
    const recentHistory = this.memoryHistory.slice(-10);
    const memoryTrend = this.calculateMemoryTrend(recentHistory);

    return {
      current: {
        rss: Math.round(currentMemory.rss / 1024 / 1024),
        heapUsed: Math.round(currentMemory.heapUsed / 1024 / 1024),
        heapTotal: Math.round(currentMemory.heapTotal / 1024 / 1024),
        external: Math.round(currentMemory.external / 1024 / 1024),
        arrayBuffers: Math.round(currentMemory.arrayBuffers / 1024 / 1024)
      },
      trend: memoryTrend,
      uptime: Math.round(uptime),
      gcCount: this.gcCount || 0,
      lastGC: new Date(this.lastGCTime).toISOString(),
      thresholds: {
        warning: this.options.warningThresholdMB,
        critical: this.options.forceGCThresholdMB,
        maximum: this.options.maxMemoryMB
      },
      history: recentHistory
    };
  }

  /**
   * ë©”ëª¨ë¦¬ íŠ¸ë Œë“œ ê³„ì‚°
   */
  calculateMemoryTrend(history) {
    if (history.length < 2) return 'stable';

    const recent = history.slice(-3);
    const older = history.slice(-6, -3);

    if (recent.length === 0 || older.length === 0) return 'stable';

    const recentAvg = recent.reduce((sum, stat) => sum + stat.heapUsed, 0) / recent.length;
    const olderAvg = older.reduce((sum, stat) => sum + stat.heapUsed, 0) / older.length;

    const change = ((recentAvg - olderAvg) / olderAvg) * 100;

    if (change > 10) return 'increasing';
    if (change < -10) return 'decreasing';
    return 'stable';
  }

  /**
   * ì—ëŸ¬ ì²˜ë¦¬
   */
  handleError(type, error) {
    this.log(`${type}: ${error.message || error}`, 'error');
    this.emit('error', { type, error });

    // ë©”ëª¨ë¦¬ ê´€ë ¨ ì—ëŸ¬ì¸ ê²½ìš° ì •ë¦¬ ìˆ˜í–‰
    if (error.message && error.message.includes('out of memory')) {
      this.performEmergencyCleanup();
    }
  }

  /**
   * ì •ìƒ ì¢…ë£Œ
   */
  gracefulShutdown() {
    this.log('Graceful shutdown initiated', 'info');

    if (this.monitorTimer) {
      clearInterval(this.monitorTimer);
    }

    this.cleanup();

    setTimeout(() => {
      process.exit(0);
    }, 5000);
  }

  /**
   * ì •ë¦¬ ì‘ì—…
   */
  cleanup() {
    this.log('Cleaning up memory optimizer', 'info');

    this.isMonitoring = false;

    if (this.monitorTimer) {
      clearInterval(this.monitorTimer);
      this.monitorTimer = null;
    }

    this.cleanupEventListeners();
    this.cleanupTimers();

    this.emit('cleanup:final');
  }

  /**
   * ë¡œê¹…
   */
  log(message, level = 'info') {
    if (!this.options.enableLogging) return;

    const timestamp = new Date().toISOString();
    const logMessage = `[${timestamp}] [MemoryOptimizer] [${level.toUpperCase()}] ${message}`;

    if (console[level]) {
      console[level](logMessage);
    } else {
      console.log(logMessage);
    }
  }
}

module.exports = MemoryOptimizer;

